[Tool: Write] {"file_path":"C:/Users/joshl/date-app/frontend/stores/authStore.ts","content":"import { create } from 'zustand';
import { persist } from 'zustand/middleware';
import api, { User } from '@/lib/api';
import socketClient from '@/lib/socket';

interface AuthState {
  user: User | null;
  isAuthenticated: boolean;
  isLoading: boolean;
  error: string | null;

  // Actions
  login: (email: string, password: string) => Promise<void>;
  register: (data: { email: string; password: string; name: string; birthday: string }) => Promise<void>;
  logout: () => Promise<void>;
  loadUser: () => Promise<void>;
  updateUser: (data: Partial<User>) => void;
  clearError: () => void;
}

export const useAuthStore = create<AuthState>()(
  persist(
    (set, get) => ({
      user: null,
      isAuthenticated: false,
      isLoading: true,
      error: null,

      login: async (email: string, password: string) => {
        try {
          set({ isLoading: true, error: null });
          const { user, accessToken, refreshToken } = await api.login(email, password);

          api.setAccessToken(accessToken);
          api.setRefreshToken(refreshToken);

          socketClient.connect(accessToken);

          set({ user, isAuthenticated: true, isLoading: false });
        } catch (error: unknown) {
          const err = error as { message?: string };
          set({
            error: err.message || 'Login failed',
            isLoading: false
          });
          throw error;
        }
      },

      register: async (data: { email: string; password: string; name: string; birthday: string }) => {
        try {
          set({ isLoading: true, error: null });
          const { user, accessToken, refreshToken } = await api.register(data);

          api.setAccessToken(accessToken);
          api.setRefreshToken(refreshToken);

          socketClient.connect(accessToken);

          set({ user, isAuthenticated: true, isLoading: false });
        } catch (error: unknown) {
          const err = error as { message?: string };
          set({
            error: err.message || 'Registration failed',
            isLoading: false
          });
          throw error;
        }
      },

      logout: async () => {
        try {
          await api.logout();
        } catch {
          // Ignore logout errors
        } finally {
          socketClient.disconnect();
          api.clearTokens();
          set({ user: null, isAuthenticated: false, isLoading: false });
        }
      },

      loadUser: async () => {
        try {
          set({ isLoading: true });
          api.loadTokenFromStorage();

          const user = await api.getCurrentUser();

          const accessToken = localStorage.getItem('accessToken');
          if (accessToken) {
            socketClient.connect(accessToken);
          }

          set({ user, isAuthenticated: true, isLoading: false });
        } catch {
          set({ user: null, isAuthenticated: false, isLoading: false });
        }
      },

      updateUser: (data: Partial<User>) => {
        const currentUser = get().user;
        if (currentUser) {
          set({ user: { ...currentUser, ...data } });
        }
      },

      clearError: () => set({ error: null }),
    }),
    {
      name: 'auth-storage',
      partialize: (state) => ({
        user: state.user,
        isAuthenticated: state.isAuthenticated
      }),
    }
  )
);
"}
[Tool: Write] {"file_path":"C:/Users/joshl/date-app/frontend/stores/discoverStore.ts","content":"import { create } from 'zustand';
import api, { DiscoverProfile, Match } from '@/lib/api';

interface DiscoverState {
  profiles: DiscoverProfile[];
  currentIndex: number;
  isLoading: boolean;
  error: string | null;
  lastSwipe: { profile: DiscoverProfile; action: 'like' | 'pass' | 'super-like' } | null;

  // Actions
  loadProfiles: () => Promise<void>;
  likeProfile: () => Promise<{ matched: boolean; match?: Match } | null>;
  passProfile: () => Promise<void>;
  superLikeProfile: () => Promise<{ matched: boolean; match?: Match } | null>;
  undoSwipe: () => Promise<void>;
  nextProfile: () => void;
  clearError: () => void;
}

export const useDiscoverStore = create<DiscoverState>((set, get) => ({
  profiles: [],
  currentIndex: 0,
  isLoading: false,
  error: null,
  lastSwipe: null,

  loadProfiles: async () => {
    try {
      set({ isLoading: true, error: null });
      const profiles = await api.getProfiles({ limit: 20 });
      set({ profiles, currentIndex: 0, isLoading: false });
    } catch (error: unknown) {
      const err = error as { message?: string };
      set({
        error: err.message || 'Failed to load profiles',
        isLoading: false
      });
    }
  },

  likeProfile: async () => {
    const { profiles, currentIndex } = get();
    const currentProfile = profiles[currentIndex];

    if (!currentProfile) return null;

    try {
      set({
        lastSwipe: { profile: currentProfile, action: 'like' }
      });

      const result = await api.likeProfile(currentProfile.id);
      get().nextProfile();
      return result;
    } catch (error: unknown) {
      const err = error as { message?: string };
      set({ error: err.message || 'Failed to like profile' });
      return null;
    }
  },

  passProfile: async () => {
    const { profiles, currentIndex } = get();
    const currentProfile = profiles[currentIndex];

    if (!currentProfile) return;

    try {
      set({
        lastSwipe: { profile: currentProfile, action: 'pass' }
      });

      await api.passProfile(currentProfile.id);
      get().nextProfile();
    } catch (error: unknown) {
      const err = error as { message?: string };
      set({ error: err.message || 'Failed to pass profile' });
    }
  },

  superLikeProfile: async () => {
    const { profiles, currentIndex } = get();
    const currentProfile = profiles[currentIndex];

    if (!currentProfile) return null;

    try {
      set({
        lastSwipe: { profile: currentProfile, action: 'super-like' }
      });

      const result = await api.superLikeProfile(currentProfile.id);
      get().nextProfile();
      return result;
    } catch (error: unknown) {
      const err = error as { message?: string };
      set({ error: err.message || 'Failed to super like profile' });
      return null;
    }
  },

  undoSwipe: async () => {
    const { lastSwipe, profiles, currentIndex } = get();

    if (!lastSwipe) return;

    try {
      await api.undoSwipe();

      // Add the profile back to the beginning of the remaining profiles
      const newProfiles = [...profiles];
      newProfiles.splice(currentIndex, 0, lastSwipe.profile);

      set({
        profiles: newProfiles,
        lastSwipe: null
      });
    } catch (error: unknown) {
      const err = error as { message?: string };
      set({ error: err.message || 'Failed to undo swipe' });
    }
  },

  nextProfile: () => {
    const { currentIndex, profiles } = get();
    const newIndex = currentIndex + 1;

    // Load more profiles when running low
    if (newIndex >= profiles.length - 3) {
      get().loadProfiles();
    }

    set({ currentIndex: newIndex });
  },

  clearError: () => set({ error: null }),
}));
"}

