[Tool: Write] {"file_path":"C:/Users/joshl/date-app/backend/prisma/schema.prisma","content":"generator client {
  provider = "prisma-client-js"
}

datasource db {
  provider = "postgresql"
  url      = env("DATABASE_URL")
}

enum Gender {
  MALE
  FEMALE
  NON_BINARY
  OTHER
}

enum LookingFor {
  RELATIONSHIP
  FRIENDSHIP
  CASUAL
  NETWORKING
}

enum MatchStatus {
  PENDING
  MATCHED
  UNMATCHED
}

enum MessageStatus {
  SENT
  DELIVERED
  READ
}

enum ReportReason {
  SPAM
  HARASSMENT
  INAPPROPRIATE_CONTENT
  FAKE_PROFILE
  UNDERAGE
  OTHER
}

enum NotificationType {
  NEW_MATCH
  NEW_MESSAGE
  NEW_LIKE
  PROFILE_VIEW
  SYSTEM
}

model User {
  id                String    @id @default(uuid())
  email             String    @unique
  password          String
  isEmailVerified   Boolean   @default(false)
  isActive          Boolean   @default(true)
  isPremium         Boolean   @default(false)
  premiumExpiresAt  DateTime?
  lastLoginAt       DateTime?
  createdAt         DateTime  @default(now())
  updatedAt         DateTime  @updatedAt

  profile           Profile?
  photos            Photo[]
  sentLikes         Like[]           @relation("SentLikes")
  receivedLikes     Like[]           @relation("ReceivedLikes")
  matchesAsUser1    Match[]          @relation("MatchUser1")
  matchesAsUser2    Match[]          @relation("MatchUser2")
  sentMessages      Message[]        @relation("SentMessages")
  receivedMessages  Message[]        @relation("ReceivedMessages")
  blockedUsers      Block[]          @relation("BlockedBy")
  blockedByUsers    Block[]          @relation("BlockedUsers")
  sentReports       Report[]         @relation("Reporter")
  receivedReports   Report[]         @relation("Reported")
  notifications     Notification[]
  refreshTokens     RefreshToken[]
  profileViews      ProfileView[]    @relation("Viewer")
  viewedProfiles    ProfileView[]    @relation("Viewed")
  preferences       UserPreference?
  conversations     ConversationParticipant[]

  @@index([email])
  @@index([isActive])
}

model Profile {
  id              String      @id @default(uuid())
  userId          String      @unique
  firstName       String
  lastName        String?
  displayName     String?
  bio             String?     @db.Text
  birthDate       DateTime
  gender          Gender
  lookingFor      LookingFor[]
  interests       String[]
  occupation      String?
  education       String?
  height          Int?        // in centimeters
  location        String?
  latitude        Float?
  longitude       Float?
  city            String?
  country         String?
  isVisible       Boolean     @default(true)
  lastActiveAt    DateTime    @default(now())
  profileComplete Boolean     @default(false)
  createdAt       DateTime    @default(now())
  updatedAt       DateTime    @updatedAt

  user            User        @relation(fields: [userId], references: [id], onDelete: Cascade)

  @@index([gender])
  @@index([location])
  @@index([isVisible])
  @@index([latitude, longitude])
}

model Photo {
  id          String    @id @default(uuid())
  userId      String
  url         String
  publicId    String?   // for cloud storage reference
  isPrimary   Boolean   @default(false)
  order       Int       @default(0)
  isVerified  Boolean   @default(false)
  createdAt   DateTime  @default(now())

  user        User      @relation(fields: [userId], references: [id], onDelete: Cascade)

  @@index([userId])
  @@index([isPrimary])
}

model UserPreference {
  id              String      @id @default(uuid())
  userId          String      @unique
  minAge          Int         @default(18)
  maxAge          Int         @default(100)
  maxDistance     Int         @default(100) // in kilometers
  genderPreference Gender[]
  lookingFor      LookingFor[]
  showOnlineStatus Boolean    @default(true)
  showDistance    Boolean     @default(true)
  pushNotifications Boolean   @default(true)
  emailNotifications Boolean  @default(true)
  createdAt       DateTime    @default(now())
  updatedAt       DateTime    @updatedAt

  user            User        @relation(fields: [userId], references: [id], onDelete: Cascade)
}

model Like {
  id          String    @id @default(uuid())
  fromUserId  String
  toUserId    String
  isSuperLike Boolean   @default(false)
  createdAt   DateTime  @default(now())

  fromUser    User      @relation("SentLikes", fields: [fromUserId], references: [id], onDelete: Cascade)
  toUser      User      @relation("ReceivedLikes", fields: [toUserId], references: [id], onDelete: Cascade)

  @@unique([fromUserId, toUserId])
  @@index([toUserId])
}

model Match {
  id          String      @id @default(uuid())
  user1Id     String
  user2Id     String
  status      MatchStatus @default(MATCHED)
  matchedAt   DateTime    @default(now())
  unmatchedAt DateTime?
  unmatchedBy String?

  user1       User        @relation("MatchUser1", fields: [user1Id], references: [id], onDelete: Cascade)
  user2       User        @relation("MatchUser2", fields: [user2Id], references: [id], onDelete: Cascade)
  conversation Conversation?

  @@unique([user1Id, user2Id])
  @@index([user1Id])
  @@index([user2Id])
}

model Conversation {
  id            String    @id @default(uuid())
  matchId       String    @unique
  lastMessageAt DateTime?
  createdAt     DateTime  @default(now())
  updatedAt     DateTime  @updatedAt

  match         Match     @relation(fields: [matchId], references: [id], onDelete: Cascade)
  messages      Message[]
  participants  ConversationParticipant[]

  @@index([lastMessageAt])
}

model ConversationParticipant {
  id              String    @id @default(uuid())
  conversationId  String
  userId          String
  lastReadAt      DateTime?
  isMuted         Boolean   @default(false)
  createdAt       DateTime  @default(now())

  conversation    Conversation @relation(fields: [conversationId], references: [id], onDelete: Cascade)
  user            User         @relation(fields: [userId], references: [id], onDelete: Cascade)

  @@unique([conversationId, userId])
}

model Message {
  id              String        @id @default(uuid())
  conversationId  String
  senderId        String
  receiverId      String
  content         String        @db.Text
  status          MessageStatus @default(SENT)
  isDeleted       Boolean       @default(false)
  deletedAt       DateTime?
  createdAt       DateTime      @default(now())
  updatedAt       DateTime      @updatedAt

  conversation    Conversation  @relation(fields: [conversationId], references: [id], onDelete: Cascade)
  sender          User          @relation("SentMessages", fields: [senderId], references: [id], onDelete: Cascade)
  receiver        User          @relation("ReceivedMessages", fields: [receiverId], references: [id], onDelete: Cascade)

  @@index([conversationId])
  @@index([senderId])
  @@index([receiverId])
  @@index([createdAt])
}

model Block {
  id          String    @id @default(uuid())
  blockerId   String
  blockedId   String
  reason      String?
  createdAt   DateTime  @default(now())

  blocker     User      @relation("BlockedBy", fields: [blockerId], references: [id], onDelete: Cascade)
  blocked     User      @relation("BlockedUsers", fields: [blockedId], references: [id], onDelete: Cascade)

  @@unique([blockerId, blockedId])
  @@index([blockedId])
}

model Report {
  id          String       @id @default(uuid())
  reporterId  String
  reportedId  String
  reason      ReportReason
  description String?      @db.Text
  isResolved  Boolean      @default(false)
  resolvedAt  DateTime?
  resolvedBy  String?
  createdAt   DateTime     @default(now())

  reporter    User         @relation("Reporter", fields: [reporterId], references: [id], onDelete: Cascade)
  reported    User         @relation("Reported", fields: [reportedId], references: [id], onDelete: Cascade)

  @@index([reportedId])
  @@index([isResolved])
}

model Notification {
  id          String           @id @default(uuid())
  userId      String
  type        NotificationType
  title       String
  body        String
  data        Json?
  isRead      Boolean          @default(false)
  createdAt   DateTime         @default(now())

  user        User             @relation(fields: [userId], references: [id], onDelete: Cascade)

  @@index([userId])
  @@index([isRead])
  @@index([createdAt])
}

model ProfileView {
  id        String   @id @default(uuid())
  viewerId  String
  viewedId  String
  createdAt DateTime @default(now())

  viewer    User     @relation("Viewer", fields: [viewerId], references: [id], onDelete: Cascade)
  viewed    User     @relation("Viewed", fields: [viewedId], references: [id], onDelete: Cascade)

  @@index([viewedId])
  @@index([createdAt])
}

model RefreshToken {
  id          String   @id @default(uuid())
  userId      String
  token       String   @unique
  expiresAt   DateTime
  createdAt   DateTime @default(now())
  revokedAt   DateTime?

  user        User     @relation(fields: [userId], references: [id], onDelete: Cascade)

  @@index([userId])
  @@index([token])
}
"}
[Tool: Write] {"file_path":"C:/Users/joshl/date-app/backend/src/types/index.ts","content":"import { Request } from 'express';
import { User, Profile } from '@prisma/client';

export interface AuthenticatedRequest extends Request {
  user?: {
    id: string;
    email: string;
  };
}

export interface JWTPayload {
  userId: string;
  email: string;
  iat?: number;
  exp?: number;
}

export interface TokenPair {
  accessToken: string;
  refreshToken: string;
}

export interface PaginationParams {
  page: number;
  limit: number;
  skip: number;
}

export interface PaginatedResponse<T> {
  data: T[];
  pagination: {
    page: number;
    limit: number;
    total: number;
    totalPages: number;
    hasMore: boolean;
  };
}

export interface UserWithProfile extends User {
  profile: Profile | null;
}

export interface MatchSuggestion {
  user: UserWithProfile;
  compatibilityScore: number;
  distance?: number;
  commonInterests: string[];
}

export interface SocketUser {
  id: string;
  socketId: string;
  rooms: string[];
}

export interface ChatMessage {
  id: string;
  conversationId: string;
  senderId: string;
  receiverId: string;
  content: string;
  createdAt: Date;
}

export interface TypingEvent {
  conversationId: string;
  userId: string;
  isTyping: boolean;
}

export interface OnlineStatusEvent {
  userId: string;
  isOnline: boolean;
  lastActiveAt?: Date;
}

export interface ApiResponse<T = unknown> {
  success: boolean;
  message?: string;
  data?: T;
  error?: string;
  errors?: ValidationError[];
}

export interface ValidationError {
  field: string;
  message: string;
}

export interface LocationCoordinates {
  latitude: number;
  longitude: number;
}

export interface MatchingCriteria {
  userId: string;
  minAge: number;
  maxAge: number;
  maxDistance: number;
  genderPreference: string[];
  lookingFor: string[];
  location?: LocationCoordinates;
}

export interface AIMatchingContext {
  userBio: string;
  userInterests: string[];
  candidateBio: string;
  candidateInterests: string[];
}
"}
[Tool: Write] {"file_path":"C:/Users/joshl/date-app/backend/src/utils/logger.ts","content":"import winston from 'winston';

const logFormat = winston.format.combine(
  winston.format.timestamp({ format: 'YYYY-MM-DD HH:mm:ss' }),
  winston.format.errors({ stack: true }),
  winston.format.printf(({ level, message, timestamp, stack }) => {
    return `${timestamp} [${level.toUpperCase()}]: ${stack || message}`;
  })
);

const logger = winston.createLogger({
  level: process.env.NODE_ENV === 'production' ? 'info' : 'debug',
  format: logFormat,
  transports: [
    new winston.transports.Console({
      format: winston.format.combine(
        winston.format.colorize(),
        logFormat
      ),
