      };
    } catch (error) {
      logger.error('AI content moderation error:', error);
      return { isAppropriate: true, flaggedCategories: [] };
    }
  }

  async enhanceBio(bio: string): Promise<string> {
    if (!this.openai) {
      return bio;
    }

    try {
