      req.query.page as string,
      req.query.limit as string
    );

    const [conversations, total] = await Promise.all([
      prisma.conversationParticipant.findMany({
        where: { userId },
        include: {
          conversation: {
            include: {
              participants: {
                where: { userId: { not: userId } },
                include: {
