import prisma from '../utils/prisma';
import { AuthenticatedRequest } from '../types';
import { asyncHandler, NotFoundError, BadRequestError } from '../middleware/error.middleware';
import { calculateAge } from '../utils/helpers';
import { notificationService } from '../services';

export const getProfile = asyncHandler(
  async (req: AuthenticatedRequest, res: Response): Promise<void> => {
    const { userId } = req.params;
    const currentUserId = req.user!.id;

    const user = await prisma.user.findUnique({
      where: { id: userId, isActive: true },
      select: {
        id: true,
        isPremium: true,
        profile: {
          select: {
            firstName: true,
            displayName: true,
            bio: true,
            birthDate: true,
            gender: true,
            lookingFor: true,
            interests: true,
            occupation: true,
            education: true,
            height: true,
            city: true,
            country: true,
            lastActiveAt: true,
          },
        },
        photos: {
          where: { isPrimary: true },
          take: 1,
        },
      },
    });

    if (!user || !user.profile) {
      throw new NotFoundError('Profile not found');
    }

    // Record profile view
    if (userId !== currentUserId) {
      await prisma.profileView.create({
        data: {
          viewerId: currentUserId,
          viewedId: userId,
        },
      });

      // Notify about profile view
      await notificationService.notifyProfileView(userId, currentUserId);
    }

    // Calculate age from birthDate
    const age = calculateAge(user.profile.birthDate);

    res.json({
      success: true,
      data: {
        ...user,
        profile: {
          ...user.profile,
          age,
          birthDate: undefined, // Don't expose exact birth date
        },
      },
    });
  }
);

export const updateProfile = asyncHandler(
  async (req: AuthenticatedRequest, res: Response): Promise<void> => {
    const userId = req.user!.id;
    const updateData = req.body;

    // Check if profile exists
    const existingProfile = await prisma.profile.findUnique({
      where: { userId },
    });

    if (!existingProfile) {
      throw new NotFoundError('Profile not found');
    }

    // Handle birthDate conversion
    if (updateData.birthDate) {
      updateData.birthDate = new Date(updateData.birthDate);
    }

    // Update profile
    const profile = await prisma.profile.update({
      where: { userId },
      data: {
        ...updateData,
        lastActiveAt: new Date(),
        profileComplete: true, // Mark as complete after update
      },
    });

    res.json({
      success: true,
      message: 'Profile updated successfully',
      data: profile,
    });
  }
);

export const updatePreferences = asyncHandler(
  async (req: AuthenticatedRequest, res: Response): Promise<void> => {
    const userId = req.user!.id;
    const updateData = req.body;

    const preferences = await prisma.userPreference.upsert({
      where: { userId },
      update: updateData,
      create: {
        userId,
        ...updateData,
      },
    });

    res.json({
      success: true,
      message: 'Preferences updated successfully',
      data: preferences,
    });
