import { ethers } from 'ethers';
import crypto from 'crypto';

/**
 * HeartDAO Service
 * Integrates blockchain DAO with dating platform
 *
 * FOR THE KIDS! 
 */

// Contract ABIs (simplified for essential functions)
const HEART_DAO_ABI = [
  "function distributeRevenue() external payable",
  "function donateToShriners(string message) external payable",
  "function isRoyaltyFounder(address) view returns (bool)",
  "function getRoyaltyStatus(address) view returns (bool, string)",
  "event RevenueDistributed(uint256 totalAmount, uint256 charityAmount, uint256 infraAmount, uint256 founderAmount, uint256 timestamp)"
];

const ROYALTY_NFT_ABI = [
  "function isRoyaltyHolder(address) view returns (bool)",
  "function getCardDetails(uint256) view returns (uint8, address, string)",
  "function getRemainingCards() view returns (bool, bool, bool, bool, bool)"
];

class DAOService {
  constructor() {
    this.provider = null;
    this.daoContract = null;
    this.nftContract = null;
    this.isInitialized = false;
  }

  async initialize() {
    if (this.isInitialized) return;

    const rpcUrl = process.env.BLOCKCHAIN_RPC_URL || 'https://polygon-rpc.com';
    this.provider = new ethers.JsonRpcProvider(rpcUrl);

    if (process.env.HEART_DAO_ADDRESS) {
      this.daoContract = new ethers.Contract(
        process.env.HEART_DAO_ADDRESS,
        HEART_DAO_ABI,
        this.provider
      );
    }

    if (process.env.ROYALTY_NFT_ADDRESS) {
      this.nftContract = new ethers.Contract(
        process.env.ROYALTY_NFT_ADDRESS,
        ROYALTY_NFT_ABI,
        this.provider
      );
    }

    this.isInitialized = true;
    console.log(' HeartDAO Service initialized - FOR THE KIDS!');
  }

  /**
   * Check if user is a Royalty Founder
   */
  async isRoyaltyFounder(walletAddress) {
    if (!this.nftContract) return false;
    try {
      return await this.nftContract.isRoyaltyHolder(walletAddress);
    } catch (error) {
      console.error('Error checking royalty status:', error);
      return false;
    }
  }

  /**
   * Get remaining Royalty Cards
   */
  async getRemainingCards() {
    if (!this.nftContract) {
      return {
        ace: true,
        king: true,
        queen: true,
        jack: true,
        joker: true
      };
    }

    try {
      const [ace, king, queen, jack, joker] = await this.nftContract.getRemainingCards();
      return { ace, king, queen, jack, joker };
    } catch (error) {
      console.error('Error getting remaining cards:', error);
      return { ace: true, king: true, queen: true, jack: true, joker: true };
    }
  }

  /**
   * Calculate revenue split
   */
  calculateRevenueSplit(totalAmount) {
    const total = parseFloat(totalAmount);
    return {
      total,
      charity: (total * 0.60).toFixed(2),      // 60% Shriners
      infrastructure: (total * 0.30).toFixed(2), // 30% Platform
      founder: (total * 0.10).toFixed(2)        // 10% Founder
    };
  }
}

/**
 * Pre-order Management Service
 */
class PreOrderService {
  constructor(db) {
    this.db = db;
  }

  /**
   * Create pre-order with transaction ID for Joker drawing
   */
  async createPreOrder(userId, type, paymentDetails) {
    const transactionId = this.generateTransactionId();

    const preOrder = await this.db.preOrder.create({
      data: {
        userId,
        type, // 'EARLY_BIRD' or 'ROYALTY_CARD'
        cardType: paymentDetails.cardType || null, // ACE, KING, QUEEN, JACK
        amount: paymentDetails.amount,
        transactionId,
        squarePaymentId: paymentDetails.squarePaymentId,
        status: 'CONFIRMED',
        jokerEligible: true, // All pre-orders enter Joker drawing
        createdAt: new Date()
      }
    });

    return {
      ...preOrder,
      message: ` Your transaction ID ${transactionId} is entered in the Valentine's Day Joker drawing!`
    };
  }

  /**
   * Generate unique transaction ID for Joker drawing
   */
  generateTransactionId() {
    const timestamp = Date.now().toString(36);
    const random = crypto.randomBytes(4).toString('hex');
    return `HEART-${timestamp}-${random}`.toUpperCase();
  }

  /**
   * Get all Joker-eligible entries
   */
  async getJokerDrawingEntries() {
    return await this.db.preOrder.findMany({
      where: {
        jokerEligible: true,
        status: 'CONFIRMED'
      },
      select: {
        transactionId: true,
        userId: true,
        type: true,
        createdAt: true
      }
    });
  }

  /**
   * Perform Joker random drawing
   * Uses cryptographically secure randomness
   */
  async performJokerDrawing() {
    const entries = await this.getJokerDrawingEntries();

    if (entries.length === 0) {
      throw new Error('No eligible entries for Joker drawing');
    }

    // Cryptographically secure random selection
    const randomBytes = crypto.randomBytes(4);
    const randomIndex = randomBytes.readUInt32BE(0) % entries.length;

    const winner = entries[randomIndex];

    // Record the drawing result
    await this.db.jokerDrawing.create({
      data: {
        winnerTransactionId: winner.transactionId,
        winnerUserId: winner.userId,
        totalEntries: entries.length,
        drawnAt: new Date(),
        verificationHash: crypto.createHash('sha256')
          .update(JSON.stringify(entries.map(e => e.transactionId)))
          .digest('hex')
      }
    });

    return winner;
  }

  /**
   * Get pre-order statistics
   */
  async getPreOrderStats() {
    const [earlyBird, royalty, total] = await Promise.all([
      this.db.preOrder.count({ where: { type: 'EARLY_BIRD' } }),
      this.db.preOrder.count({ where: { type: 'ROYALTY_CARD' } }),
      this.db.preOrder.count()
    ]);

    return {
      earlyBirdCount: earlyBird,
      earlyBirdRemaining: Math.max(0, 100 - earlyBird), // 100 early bird limit
      royaltyCardsSold: royalty,
      royaltyCardsRemaining: Math.max(0, 4 - royalty), // 4 cards (Joker is drawing)
      totalPreOrders: total,
      jokerEntries: total // All pre-orders = Joker entries
    };
  }
}

export const daoService = new DAOService();
export { PreOrderService };

import express from 'express';
import { PreOrderService, daoService } from '../services/daoService.js';
import { prisma } from '../lib/prisma.js';

const router = express.Router();
const preOrderService = new PreOrderService(prisma);

/**
 * Pre-Order Routes for Royalty Deck of Hearts
 *
 * FOR THE KIDS! 
 */

/**
 * GET /api/preorder/stats
 * Get current pre-order statistics
 */
router.get('/stats', async (req, res) => {
  try {
    const stats = await preOrderService.getPreOrderStats();
    const remainingCards = await daoService.getRemainingCards();

    res.json({
      success: true,
      data: {
        ...stats,
        availableCards: remainingCards,
        launchDate: '2026-02-14T00:00:00Z',
        daysUntilLaunch: Math.ceil(
          (new Date('2026-02-14') - new Date()) / (1000 * 60 * 60 * 24)
        )
      }
    });
  } catch (error) {
    console.error('Pre-order stats error:', error);
    res.status(500).json({ success: false, error: 'Failed to get stats' });
  }
});

/**
 * POST /api/preorder/early-bird
 * Create Early Bird pre-order ($4.99/mo for life)
 */
router.post('/early-bird', async (req, res) => {
  try {
    const { userId, squarePaymentId } = req.body;

    // Check if early bird slots available
    const stats = await preOrderService.getPreOrderStats();
    if (stats.earlyBirdRemaining <= 0) {
      return res.status(400).json({
        success: false,
        error: 'All 100 Early Bird slots have been claimed!'
      });
    }

    const preOrder = await preOrderService.createPreOrder(userId, 'EARLY_BIRD', {
      amount: 4.99,
      squarePaymentId
    });

    res.json({
      success: true,
      data: preOrder,
      message: ' Welcome to the Early Bird family! $5 off FOR LIFE!',
      jokerEntry: preOrder.transactionId
    });
  } catch (error) {
    console.error('Early bird pre-order error:', error);
    res.status(500).json({ success: false, error: 'Pre-order failed' });
  }
});

/**
 * POST /api/preorder/royalty-card
 * Purchase a Royalty Founder Card ($1,000)
 */
router.post('/royalty-card', async (req, res) => {
  try {
    const { userId, cardType, squarePaymentId } = req.body;

    // Validate card type
    const validCards = ['ACE', 'KING', 'QUEEN', 'JACK'];
    if (!validCards.includes(cardType.toUpperCase())) {
      return res.status(400).json({
        success: false,
        error: 'Invalid card type. Choose: ACE, KING, QUEEN, or JACK'
      });
    }

    // Check card availability
    const remainingCards = await daoService.getRemainingCards();
    const cardKey = cardType.toLowerCase();
    if (!remainingCards[cardKey]) {
      return res.status(400).json({
        success: false,
        error: `The ${cardType} of Hearts has already been claimed!`
      });
    }

    const preOrder = await preOrderService.createPreOrder(userId, 'ROYALTY_CARD', {
      cardType: cardType.toUpperCase(),
      amount: 1000,
      squarePaymentId
    });

    res.json({
      success: true,
      data: preOrder,
      message: ` Congratulations! You are the ${cardType} of Hearts! Premium FREE FOR LIFE + Unlimited Super Likes!`,
      jokerEntry: preOrder.transactionId,
      benefits: [
        'Unique animated card border',
        'Semi-transparent rising hearts effect',
        'Broken heart shatter animation on swipe-left',
        '3 FREE Heart Shards on launch day',
        'Premium membership FREE FOR LIFE',
        'UNLIMITED Heart Shards forever',
        'DAO voting rights',
        'Founder badge on profile'
      ]
    });
  } catch (error) {
    console.error('Royalty card pre-order error:', error);
    res.status(500).json({ success: false, error: 'Pre-order failed' });
  }
});

/**
 * GET /api/preorder/joker-entries
 * Get total Joker drawing entries (public stats)
 */
router.get('/joker-entries', async (req, res) => {
  try {
    const stats = await preOrderService.getPreOrderStats();

    res.json({
      success: true,
      data: {
        totalEntries: stats.jokerEntries,
        drawingDate: '2026-02-14T00:00:00Z',
        prize: 'The Joker\'s Heart - Design your own 1-of-1 animated card with Opus AI'
      }
    });
  } catch (error) {
    console.error('Joker entries error:', error);
    res.status(500).json({ success: false, error: 'Failed to get entries' });
  }
});

/**
 * POST /api/preorder/joker-drawing
 * Perform the Joker drawing (admin only, Valentine's Day)
 */
router.post('/joker-drawing', async (req, res) => {
  try {
    // TODO: Add admin authentication
    const { adminSecret } = req.body;
    if (adminSecret !== process.env.ADMIN_SECRET) {
      return res.status(403).json({ success: false, error: 'Unauthorized' });
    }

    const winner = await preOrderService.performJokerDrawing();

    res.json({
      success: true,
      data: {
        winner,
        message: ' The Joker\'s Heart has found its owner!',
        nextStep: 'Contact winner to design their custom animation with Opus AI'
      }
    });
  } catch (error) {
    console.error('Joker drawing error:', error);
    res.status(500).json({ success: false, error: 'Drawing failed' });
  }
});

/**
 * GET /api/preorder/revenue-split
 * Show the Gospel V1.3 revenue split
 */
router.get('/revenue-split', async (req, res) => {
  res.json({
    success: true,
    data: {
      split: {
        shrinersChildrensHospital: '60%',
        platformInfrastructure: '30%',
        founderSustainability: '10%'
      },
      mission: 'FOR THE KIDS!',
      governance: 'All allocations governed by HeartDAO',
      transparency: 'Every transaction on-chain and auditable'
    }
  });
});

export default router;

