    const { userId, squarePaymentId } = req.body;

    // Check if early bird slots available
    const stats = await preOrderService.getPreOrderStats();
    if (stats.earlyBirdRemaining <= 0) {
      return res.status(400).json({
        success: false,
        error: 'All 100 Early Bird slots have been claimed!'
      });
    }

    const preOrder = await preOrderService.createPreOrder(userId, 'EARLY_BIRD', {
      amount: 4.99,
      squarePaymentId
    });

    res.json({
      success: true,
      data: preOrder,
      message: ' Welcome to the Early Bird family! $5 off FOR LIFE!',
      jokerEntry: preOrder.transactionId
    });
  } catch (error) {
    console.error('Early bird pre-order error:', error);
    res.status(500).json({ success: false, error: 'Pre-order failed' });
  }
});

/**
 * POST /api/preorder/royalty-card
 * Purchase a Royalty Founder Card ($1,000)
 */
router.post('/royalty-card', async (req, res) => {
  try {
    const { userId, cardType, squarePaymentId } = req.body;

    // Validate card type
    const validCards = ['ACE', 'KING', 'QUEEN', 'JACK'];
    if (!validCards.includes(cardType.toUpperCase())) {
      return res.status(400).json({
        success: false,
        error: 'Invalid card type. Choose: ACE, KING, QUEEN, or JACK'
      });
    }

    // Check card availability
    const remainingCards = await daoService.getRemainingCards();
    const cardKey = cardType.toLowerCase();
    if (!remainingCards[cardKey]) {
      return res.status(400).json({
        success: false,
        error: `The ${cardType} of Hearts has already been claimed!`
      });
    }

    const preOrder = await preOrderService.createPreOrder(userId, 'ROYALTY_CARD', {
      cardType: cardType.toUpperCase(),
      amount: 1000,
      squarePaymentId
    });

    res.json({
      success: true,
      data: preOrder,
      message: ` Congratulations! You are the ${cardType} of Hearts! Premium FREE FOR LIFE + Unlimited Super Likes!`,
      jokerEntry: preOrder.transactionId,
      benefits: [
        'Unique animated card border',
        'Semi-transparent rising hearts effect',
        'Broken heart shatter animation on swipe-left',
        '3 FREE Heart Shards on launch day',
        'Premium membership FREE FOR LIFE',
        'UNLIMITED Heart Shards forever',
        'DAO voting rights',
        'Founder badge on profile'
      ]
    });
  } catch (error) {
    console.error('Royalty card pre-order error:', error);
    res.status(500).json({ success: false, error: 'Pre-order failed' });
  }
});

/**
 * GET /api/preorder/joker-entries
 * Get total Joker drawing entries (public stats)
 */
router.get('/joker-entries', async (req, res) => {
  try {
    const stats = await preOrderService.getPreOrderStats();

    res.json({
      success: true,
      data: {
        totalEntries: stats.jokerEntries,
        drawingDate: '2026-02-14T00:00:00Z',
        prize: 'The Joker\'s Heart - Design your own 1-of-1 animated card with Opus AI'
      }
    });
  } catch (error) {
    console.error('Joker entries error:', error);
    res.status(500).json({ success: false, error: 'Failed to get entries' });
  }
});

/**
 * POST /api/preorder/joker-drawing
 * Perform the Joker drawing (admin only, Valentine's Day)
 */
router.post('/joker-drawing', async (req, res) => {
  try {
    // TODO: Add admin authentication
    const { adminSecret } = req.body;
    if (adminSecret !== process.env.ADMIN_SECRET) {
      return res.status(403).json({ success: false, error: 'Unauthorized' });
    }

    const winner = await preOrderService.performJokerDrawing();

    res.json({
      success: true,
      data: {
        winner,
        message: ' The Joker\'s Heart has found its owner!',
        nextStep: 'Contact winner to design their custom animation with Opus AI'
      }
    });
  } catch (error) {
    console.error('Joker drawing error:', error);
    res.status(500).json({ success: false, error: 'Drawing failed' });
  }
});

/**
 * GET /api/preorder/revenue-split
 * Show the Gospel V1.3 revenue split
 */
router.get('/revenue-split', async (req, res) => {
  res.json({
    success: true,
    data: {
      split: {
        shrinersChildrensHospital: '60%',
        platformInfrastructure: '30%',
        founderSustainability: '10%'
      },
      mission: 'FOR THE KIDS!',
      governance: 'All allocations governed by HeartDAO',
      transparency: 'Every transaction on-chain and auditable'
    }
  });
});

export default router;

input.content:
/**
 * Royalty Deck of Hearts - Card Animations
 *
 * 5 Unique Founder Cards:
 * - Ace of Hearts: Golden crown rising hearts
 * - King of Hearts: Royal crimson pulse
 * - Queen of Hearts: Pink sparkle cascade
 * - Jack of Hearts: Electric blue surge
 * - Joker: Custom (winner's choice)
 *
 * FOR THE KIDS! 
 */

/* Base Card Styles */
.royalty-card-wrapper {
  position: relative;
  width: 100%;
  max-width: 400px;
  aspect-ratio: 3/4;
  perspective: 1000px;
}

.royalty-card {
  position: relative;
  width: 100%;
  height: 100%;
  border-radius: 24px;
  overflow: visible;
  background: linear-gradient(145deg, #1a1a1a, #0d0d0d);
  box-shadow: 0 25px 50px rgba(0, 0, 0, 0.5);
}

/* Animated Border Container */
.royalty-border {
  position: absolute;
  top: -4px;
  left: -4px;
  right: -4px;
  bottom: -4px;
  border-radius: 28px;
  z-index: -1;
  overflow: hidden;
}

/* ============================================
   ACE OF HEARTS - Golden Crown Rising Hearts
   ============================================ */
.royalty-card.ace .royalty-border {
  background: linear-gradient(
    45deg,
    #FFD700, #FFA500, #FFD700, #FFEC8B, #FFD700
  );
  background-size: 400% 400%;
  animation: aceGlow 3s ease infinite;
}

@keyframes aceGlow {
  0%, 100% { background-position: 0% 50%; }
  50% { background-position: 100% 50%; }
}

.royalty-card.ace .rising-hearts .heart {
  color: #FFD700;
  text-shadow: 0 0 20px rgba(255, 215, 0, 0.8);
}

/* ============================================
   KING OF HEARTS - Royal Crimson Pulse
   ============================================ */
.royalty-card.king .royalty-border {
  background: linear-gradient(
    45deg,
    #8B0000, #DC143C, #8B0000, #FF0000, #8B0000
  );
  background-size: 400% 400%;
  animation: kingPulse 2s ease infinite;
}

@keyframes kingPulse {
  0%, 100% {
    background-position: 0% 50%;
    box-shadow: 0 0 30px rgba(220, 20, 60, 0.5);
  }
  50% {
    background-position: 100% 50%;
    box-shadow: 0 0 60px rgba(220, 20, 60, 0.8);
  }
}

.royalty-card.king .rising-hearts .heart {
  color: #DC143C;
  text-shadow: 0 0 20px rgba(220, 20, 60, 0.8);
}

/* ============================================
   QUEEN OF HEARTS - Pink Sparkle Cascade
   ============================================ */
.royalty-card.queen .royalty-border {
  background: linear-gradient(
    45deg,
    #FF69B4, #FF1493, #FF69B4, #FFB6C1, #FF69B4
  );
  background-size: 400% 400%;
  animation: queenSparkle 2.5s ease infinite;
}

@keyframes queenSparkle {
  0%, 100% { background-position: 0% 50%; filter: brightness(1); }
  25% { filter: brightness(1.3); }
  50% { background-position: 100% 50%; filter: brightness(1); }
  75% { filter: brightness(1.2); }
}

.royalty-card.queen .rising-hearts .heart {
  color: #FF69B4;
  text-shadow: 0 0 20px rgba(255, 105, 180, 0.8);
}

.royalty-card.queen::after {
  content: '';
  position: absolute;
  font-size: 20px;
  animation: sparkleFloat 3s ease-in-out infinite;
  pointer-events: none;
}

@keyframes sparkleFloat {
  0%, 100% {
    top: 10%; left: 10%; opacity: 0;
  }
  25% {
    top: 30%; left: 80%; opacity: 1;
  }
  50% {
    top: 60%; left: 20%; opacity: 0.5;
  }
  75% {
    top: 80%; left: 70%; opacity: 1;
  }
}

/* ============================================
   JACK OF HEARTS - Electric Blue Surge
   ============================================ */
.royalty-card.jack .royalty-border {
  background: linear-gradient(
    45deg,
    #4169E1, #00BFFF, #4169E1, #87CEEB, #4169E1
  );
  background-size: 400% 400%;
  animation: jackSurge 1.5s ease infinite;
}

@keyframes jackSurge {
  0%, 100% {
    background-position: 0% 50%;
    transform: scale(1);
  }
  50% {
    background-position: 100% 50%;
    transform: scale(1.02);
  }
}

.royalty-card.jack .rising-hearts .heart {
  color: #00BFFF;
  text-shadow: 0 0 20px rgba(0, 191, 255, 0.8);
}

/* ============================================
   JOKER - Custom Animation (Winner's Choice)
   ============================================ */
.royalty-card.joker .royalty-border {
  background: linear-gradient(
    45deg,
    #9400D3, #FFD700, #FF1493, #00FF00, #9400D3
  );
  background-size: 600% 600%;
  animation: jokerChaos 4s ease infinite;
}

@keyframes jokerChaos {
  0% { background-position: 0% 50%; transform: rotate(0deg); }
  25% { background-position: 50% 100%; }
  50% { background-position: 100% 50%; transform: rotate(1deg); }
  75% { background-position: 50% 0%; }
  100% { background-position: 0% 50%; transform: rotate(0deg); }
}

.royalty-card.joker .rising-hearts .heart {
  animation: jokerHeartColor 2s ease infinite;
}

@keyframes jokerHeartColor {
  0%, 100% { color: #9400D3; }
  25% { color: #FFD700; }
  50% { color: #FF1493; }
  75% { color: #00FF00; }
}

/* ============================================
   RISING HEARTS EFFECT (All Cards)
   ============================================ */
.rising-hearts {
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  pointer-events: none;
  overflow: hidden;
  border-radius: 24px;
}

.rising-hearts .heart {
  position: absolute;
  bottom: -50px;
  font-size: 24px;
  opacity: 0;
  animation: riseHeart 4s ease-in-out infinite;
}

.rising-hearts .heart:nth-child(1) { left: 10%; animation-delay: 0s; }
.rising-hearts .heart:nth-child(2) { left: 25%; animation-delay: 0.5s; }
.rising-hearts .heart:nth-child(3) { left: 40%; animation-delay: 1s; }
.rising-hearts .heart:nth-child(4) { left: 55%; animation-delay: 1.5s; }
.rising-hearts .heart:nth-child(5) { left: 70%; animation-delay: 2s; }
.rising-hearts .heart:nth-child(6) { left: 85%; animation-delay: 2.5s; }

@keyframes riseHeart {
  0% {
    transform: translateY(0) scale(0.5) rotate(0deg);
    opacity: 0;
  }
  10% {
    opacity: 0.7;
  }
  50% {
    opacity: 0.5;
  }
  100% {
    transform: translateY(-500px) scale(1.2) rotate(360deg);
    opacity: 0;
  }
}

/* ============================================
   BROKEN HEART SHATTER ANIMATION
   (When someone swipes left on a Royalty card)
   ============================================ */
.shatter-container {
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  pointer-events: none;
  z-index: 9999;
  display: none;
}

.shatter-container.active {
  display: block;
}

.shatter-heart {
  position: absolute;
  top: 50%;
  left: 50%;
  font-size: 120px;
  transform: translate(-50%, -50%);
  animation: heartBreak 0.5s ease forwards;
}

@keyframes heartBreak {
  0% {
    transform: translate(-50%, -50%) scale(1);
    opacity: 1;
  }
  30% {
    transform: translate(-50%, -50%) scale(1.5);
    opacity: 1;
  }
  100% {
    transform: translate(-50%, -50%) scale(0);
    opacity: 0;
  }
}

.shard {
  position: absolute;
  font-size: 40px;
  opacity: 0;
  animation: shardExplode 1.5s ease forwards;
}

.shard:nth-child(1) { animation-delay: 0.3s; }
.shard:nth-child(2) { animation-delay: 0.35s; }
.shard:nth-child(3) { animation-delay: 0.4s; }
.shard:nth-child(4) { animation-delay: 0.45s; }
.shard:nth-child(5) { animation-delay: 0.5s; }
.shard:nth-child(6) { animation-delay: 0.55s; }

@keyframes shardExplode {
  0% {
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%) rotate(0deg);
    opacity: 1;
  }
  100% {
    opacity: 0;
  }
}

.shard:nth-child(1) {
  animation-name: shard1;
}
@keyframes shard1 {
  0% { top: 50%; left: 50%; opacity: 1; transform: rotate(0deg); }
  100% { top: 20%; left: 20%; opacity: 0; transform: rotate(-180deg); }
}

.shard:nth-child(2) {
  animation-name: shard2;
}
@keyframes shard2 {
  0% { top: 50%; left: 50%; opacity: 1; transform: rotate(0deg); }
  100% { top: 10%; left: 50%; opacity: 0; transform: rotate(90deg); }
}

.shard:nth-child(3) {
  animation-name: shard3;
}
@keyframes shard3 {
  0% { top: 50%; left: 50%; opacity: 1; transform: rotate(0deg); }
  100% { top: 20%; left: 80%; opacity: 0; transform: rotate(180deg); }
}

.shard:nth-child(4) {
  animation-name: shard4;
}
@keyframes shard4 {
  0% { top: 50%; left: 50%; opacity: 1; transform: rotate(0deg); }
  100% { top: 80%; left: 20%; opacity: 0; transform: rotate(-90deg); }
}

.shard:nth-child(5) {
  animation-name: shard5;
}
@keyframes shard5 {
  0% { top: 50%; left: 50%; opacity: 1; transform: rotate(0deg); }
  100% { top: 90%; left: 50%; opacity: 0; transform: rotate(270deg); }
}

.shard:nth-child(6) {
  animation-name: shard6;
}
@keyframes shard6 {
  0% { top: 50%; left: 50%; opacity: 1; transform: rotate(0deg); }
  100% { top: 80%; left: 80%; opacity: 0; transform: rotate(-270deg); }
}

/* Reward notification after shatter */
.shard-reward {
  position: fixed;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  background: linear-gradient(135deg, #FFD700, #FFA500);
  color: #000;
  padding: 20px 40px;
  border-radius: 50px;
  font-weight: bold;
  font-size: 18px;
  opacity: 0;
  animation: rewardPop 2s ease forwards;
  animation-delay: 1s;
  z-index: 10000;
  box-shadow: 0 10px 40px rgba(255, 215, 0, 0.5);
}

@keyframes rewardPop {
  0% {
    opacity: 0;
    transform: translate(-50%, -50%) scale(0.5);
  }
  20% {
    opacity: 1;
    transform: translate(-50%, -50%) scale(1.1);
  }
  40% {
    transform: translate(-50%, -50%) scale(1);
  }
  80% {
    opacity: 1;
  }
  100% {
    opacity: 0;
    transform: translate(-50%, -50%) scale(0.8);
  }
}

/* ============================================
   FOUNDER BADGE
   ============================================ */
.founder-badge {
  position: absolute;
  top: 15px;
  right: 15px;
  background: linear-gradient(135deg, #FFD700, #FFA500);
  color: #000;
  padding: 8px 16px;
  border-radius: 20px;
  font-size: 12px;
  font-weight: bold;
  text-transform: uppercase;
  letter-spacing: 1px;
  box-shadow: 0 4px 15px rgba(255, 215, 0, 0.4);
  z-index: 10;
}

.founder-badge::before {
  content: ' ';
}

/* Card type indicator */
.card-type-indicator {
  position: absolute;
  bottom: 20px;
  left: 50%;
  transform: translateX(-50%);
  font-size: 48px;
  z-index: 10;
}

/* Profile image container */
.royalty-profile-image {
  position: absolute;
  top: 60px;
  left: 50%;
  transform: translateX(-50%);
  width: 70%;
  aspect-ratio: 1;
  border-radius: 20px;
  overflow: hidden;
  border: 4px solid rgba(255, 215, 0, 0.5);
}

.royalty-profile-image img {
  width: 100%;
  height: 100%;
  object-fit: cover;
}

/* Name and info */
.royalty-info {
  position: absolute;
  bottom: 80px;
  left: 0;
  right: 0;
  text-align: center;
  color: white;
  z-index: 10;
}

.royalty-name {
  font-size: 24px;
  font-weight: bold;
  margin-bottom: 5px;
  text-shadow: 0 2px 10px rgba(0, 0, 0, 0.5);
}

.royalty-tagline {
  font-size: 14px;
  opacity: 0.8;
}

import React, { useState, useEffect } from 'react';
import './RoyaltyCard.css';

/**
 * RoyaltyCard Component
 *
 * Displays a Royalty Founder Card with unique animations
 * Only 5 will ever exist: Ace, King, Queen, Jack, Joker
 *
 * FOR THE KIDS! 
 */

const CARD_SYMBOLS = {
  ACE: '',
  KING: '',
  QUEEN: '',
  JACK: '',
  JOKER: ''
};

const RoyaltyCard = ({
  cardType = 'ACE',
  userName = 'Founder',
  tagline = 'Royalty Founder',
  profileImage = null,
  onSwipeLeft = null,
  onSwipeRight = null
}) => {
  const [isShattered, setIsShattered] = useState(false);
  const [showReward, setShowReward] = useState(false);

  const cardClass = cardType.toLowerCase();

  // Handle swipe left (rejection) - triggers shatter animation
  const handleSwipeLeft = () => {
    setIsShattered(true);

    // Show reward after shatter
    setTimeout(() => {
      setShowReward(true);
    }, 800);

    // Clean up and call callback
    setTimeout(() => {
      setIsShattered(false);
      setShowReward(false);
      if (onSwipeLeft) onSwipeLeft();
    }, 3000);
  };

  // Handle swipe right (like)
  const handleSwipeRight = () => {
    if (onSwipeRight) onSwipeRight();
  };

  return (
    <>
      <div className="royalty-card-wrapper">
        <div className={`royalty-card ${cardClass}`}>
          {/* Animated Border */}
          <div className="royalty-border"></div>

          {/* Rising Hearts Effect */}
          <div className="rising-hearts">
            <span className="heart"></span>
            <span className="heart"></span>
            <span className="heart"></span>
            <span className="heart"></span>
            <span className="heart"></span>
            <span className="heart"></span>
          </div>

          {/* Founder Badge */}
          <div className="founder-badge">
            {cardType} of Hearts
          </div>

          {/* Profile Image */}
          <div className="royalty-profile-image">
            {profileImage ? (
              <img src={profileImage} alt={userName} />
            ) : (
              <div style={{
                width: '100%',
                height: '100%',
                background: 'linear-gradient(135deg, #333, #111)',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                fontSize: '64px'
              }}>
                {CARD_SYMBOLS[cardType]}
              </div>
            )}
          </div>

          {/* User Info */}
          <div className="royalty-info">
            <div className="royalty-name">{userName}</div>
            <div className="royalty-tagline">{tagline}</div>
          </div>

          {/* Card Type Symbol */}
          <div className="card-type-indicator">
            {CARD_SYMBOLS[cardType]}
          </div>
        </div>

        {/* Demo Swipe Buttons (for testing) */}
        <div style={{
          display: 'flex',
          justifyContent: 'center',
          gap: '20px',
          marginTop: '20px'
        }}>
          <button
            onClick={handleSwipeLeft}
            style={{
              padding: '15px 30px',
              borderRadius: '50px',
              border: 'none',
              background: 'linear-gradient(135deg, #DC143C, #8B0000)',
              color: 'white',
              fontSize: '18px',
              cursor: 'pointer',
              boxShadow: '0 5px 20px rgba(220, 20, 60, 0.4)'
            }}
          >
             Pass
          </button>
          <button
            onClick={handleSwipeRight}
            style={{
              padding: '15px 30px',
              borderRadius: '50px',
              border: 'none',
              background: 'linear-gradient(135deg, #00C853, #00E676)',
              color: 'white',
              fontSize: '18px',
              cursor: 'pointer',
              boxShadow: '0 5px 20px rgba(0, 200, 83, 0.4)'
            }}
          >
             Like
          </button>
        </div>
      </div>

      {/* Shatter Animation Overlay */}
      <div className={`shatter-container ${isShattered ? 'active' : ''}`}>
        <div className="shatter-heart"></div>
        <span className="shard"></span>
        <span className="shard"></span>
        <span className="shard"></span>
        <span className="shard"></span>
        <span className="shard"></span>
        <span className="shard"></span>
      </div>

      {/* Reward Notification */}
      {showReward && (
        <div className="shard-reward">
          +3 Heart Shards Collected! 
        </div>
      )}
    </>
  );
};

/**
 * RoyaltyCardPreview - Shows all 5 cards in a gallery
 */
export const RoyaltyCardGallery = () => {
  return (
    <div style={{
      display: 'grid',
      gridTemplateColumns: 'repeat(auto-fit, minmax(300px, 1fr))',
      gap: '40px',
      padding: '40px',
      background: '#0a0a0a',
      minHeight: '100vh'
    }}>
      <RoyaltyCard cardType="ACE" userName="The Ace" tagline="First to Rise" />
      <RoyaltyCard cardType="KING" userName="The King" tagline="Rules the Heart" />
      <RoyaltyCard cardType="QUEEN" userName="The Queen" tagline="Grace & Power" />
      <RoyaltyCard cardType="JACK" userName="The Jack" tagline="Wild Card" />
      <RoyaltyCard cardType="JOKER" userName="The Joker" tagline="One of a Kind" />
    </div>
  );
};

export default RoyaltyCard;

