  };

  const handleDelete = async (photoId: string, e: React.MouseEvent) => {
    e.stopPropagation();
    setDeleting(photoId);
    try {
      await onDelete(photoId);
    } finally {
      setDeleting(null);
    }
  };

  // Create slots array
  const slots = Array.from({ length: maxPhotos }, (_, i) => {
    const photo = photos.find((p) => p.position === i);
    return { position: i, photo };
  });

  return (
    <div className={cn('space-y-4', className)}>
      <div className="grid grid-cols-3 gap-3">
        {slots.map(({ position, photo }) => (
          <div
            key={position}
            onClick={() => handleSlotClick(position)}
            className={cn(
              'relative aspect-square rounded-xl overflow-hidden cursor-pointer transition-all duration-200',
              photo
                ? 'bg-dark-900'
                : 'bg-dark-100 border-2 border-dashed border-dark-300 hover:border-primary-400 hover:bg-dark-50',
              position === 0 && 'col-span-2 row-span-2'
            )}
          >
            {photo ? (
              <>
                <Image
                  src={photo.url}
                  alt={`Photo ${position + 1}`}
                  fill
                  className="object-cover"
                />

                {/* Delete button */}
                <button
                  onClick={(e) => handleDelete(photo.id, e)}
                  disabled={deleting === photo.id}
                  className="absolute top-2 right-2 p-1.5 rounded-full bg-black/50 text-white hover:bg-black/70 transition-colors"
                >
                  {deleting === photo.id ? (
                    <Loader2 className="w-4 h-4 animate-spin" />
                  ) : (
                    <X className="w-4 h-4" />
                  )}
                </button>

                {/* Main photo badge */}
                {position === 0 && (
                  <div className="absolute bottom-2 left-2 px-2 py-1 rounded-md bg-black/50 text-white text-xs font-medium">
                    Main photo
                  </div>
                )}

                {/* Drag handle */}
                {onReorder && (
                  <div className="absolute top-2 left-2 p-1.5 rounded-full bg-black/50 text-white cursor-grab">
                    <GripVertical className="w-4 h-4" />
                  </div>
                )}
              </>
            ) : (
              <div className="absolute inset-0 flex flex-col items-center justify-center text-dark-400">
                {uploading === position ? (
                  <Loader2 className="w-8 h-8 animate-spin text-primary-500" />
                ) : (
                  <>
                    <Plus className="w-8 h-8" />
                    <span className="text-xs mt-1">Add photo</span>
                  </>
                )}
              </div>
            )}
          </div>
        ))}
      </div>

      <input
        ref={fileInputRef}
        type="file"
        accept="image/*"
        onChange={handleFileChange}
        className="hidden"
      />

      <p className="text-sm text-dark-500 text-center">
        Add up to {maxPhotos} photos. Your first photo will be your main profile picture.
      </p>
    </div>
  );
}

input.content:
'use client';

import { useState, useCallback } from 'react';
import { Check, Search } from 'lucide-react';
import { cn } from '@/lib/utils';
import { INTERESTS } from '@/lib/utils';
import { Input } from '@/components/ui';

interface InterestPickerProps {
  selected: string[];
  onChange: (interests: string[]) => void;
  maxInterests?: number;
  minInterests?: number;
  className?: string;
}

export function InterestPicker({
  selected,
  onChange,
  maxInterests = 10,
  minInterests = 3,
  className,
}: InterestPickerProps) {
  const [searchQuery, setSearchQuery] = useState('');

  const filteredInterests = searchQuery
    ? INTERESTS.filter((interest) =>
        interest.toLowerCase().includes(searchQuery.toLowerCase())
      )
    : INTERESTS;

  const toggleInterest = useCallback(
    (interest: string) => {
      if (selected.includes(interest)) {
        onChange(selected.filter((i) => i !== interest));
      } else if (selected.length < maxInterests) {
        onChange([...selected, interest]);
      }
    },
    [selected, onChange, maxInterests]
  );

  return (
    <div className={cn('space-y-4', className)}>
      {/* Search */}
      <Input
        placeholder="Search interests..."
        value={searchQuery}
        onChange={(e) => setSearchQuery(e.target.value)}
        leftIcon={<Search className="w-5 h-5" />}
      />

      {/* Counter */}
      <div className="flex items-center justify-between text-sm">
        <span className="text-dark-500">
          Select {minInterests}-{maxInterests} interests
        </span>
        <span
          className={cn(
            'font-medium',
            selected.length >= minInterests
              ? 'text-green-500'
              : 'text-dark-500'
          )}
        >
          {selected.length}/{maxInterests} selected
        </span>
      </div>

      {/* Progress bar */}
      <div className="progress-bar">
        <div
          className="progress-bar-fill"
          style={{
            width: `${Math.min((selected.length / maxInterests) * 100, 100)}%`,
          }}
        />
      </div>

      {/* Interests grid */}
      <div className="flex flex-wrap gap-2">
        {filteredInterests.map((interest) => {
          const isSelected = selected.includes(interest);
          const isDisabled = !isSelected && selected.length >= maxInterests;

          return (
            <button
              key={interest}
              onClick={() => toggleInterest(interest)}
              disabled={isDisabled}
              className={cn(
                'inline-flex items-center gap-1.5 px-4 py-2 rounded-full text-sm font-medium transition-all duration-200',
                isSelected
                  ? 'bg-primary-500 text-white'
                  : 'bg-dark-100 text-dark-700 hover:bg-dark-200',
                isDisabled && 'opacity-50 cursor-not-allowed'
              )}
            >
              {isSelected && <Check className="w-4 h-4" />}
              {interest}
            </button>
          );
        })}
      </div>

      {/* Selected interests preview */}
      {selected.length > 0 && (
        <div className="pt-4 border-t border-dark-100">
          <h4 className="text-sm font-medium text-dark-700 mb-2">
            Your interests:
          </h4>
          <div className="flex flex-wrap gap-2">
            {selected.map((interest) => (
              <span
                key={interest}
                className="inline-flex items-center gap-1 px-3 py-1 rounded-full text-sm bg-primary-100 text-primary-700"
              >
                {interest}
                <button
                  onClick={() => toggleInterest(interest)}
                  className="hover:text-primary-900"
                >
                  <span className="sr-only">Remove {interest}</span>
                  x
                </button>
              </span>
            ))}
          </div>
        </div>
      )}
    </div>
  );
}

