          {/* Actions */}
          {(onLike || onPass) && (
            <div className="sticky bottom-0 p-4 bg-white border-t border-dark-100 flex gap-4">
              {onPass && (
                <Button
                  variant="secondary"
                  size="lg"
                  fullWidth
                  onClick={onPass}
