/* ===== CSS Variables ===== */
:root {
    --primary-color: #FF6B6B;
    --primary-dark: #E55555;
    --primary-light: #FF8E8E;
    --secondary-color: #4ECDC4;
    --accent-color: #FFE66D;
    --dark-color: #2C3E50;
    --text-color: #333333;
    --text-light: #666666;
    --text-muted: #999999;
    --bg-color: #FFFFFF;
    --bg-light: #F8F9FA;
    --bg-gradient: linear-gradient(135deg, #FF6B6B 0%, #FF8E53 100%);
    --shadow-sm: 0 2px 10px rgba(0, 0, 0, 0.08);
    --shadow-md: 0 5px 25px rgba(0, 0, 0, 0.1);
    --shadow-lg: 0 10px 50px rgba(0, 0, 0, 0.15);
    --border-radius: 12px;
    --border-radius-lg: 24px;
    --transition: all 0.3s ease;
}

/* ===== Reset & Base ===== */
* {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
}

html {
    scroll-behavior: smooth;
}

body {
    font-family: 'Poppins', sans-serif;
    color: var(--text-color);
    line-height: 1.6;
    overflow-x: hidden;
}

.container {
    max-width: 1200px;
    margin: 0 auto;
    padding: 0 20px;
}

a {
    text-decoration: none;
    color: inherit;
}

ul {
    list-style: none;
}

/* ===== Buttons ===== */
.btn {
    display: inline-flex;
    align-items: center;
    gap: 8px;
    padding: 12px 28px;
    border-radius: 50px;
    font-weight: 600;
    font-size: 16px;
    transition: var(--transition);
    cursor: pointer;
    border: none;
}

.btn-primary {
    background: var(--bg-gradient);
    color: white;
    box-shadow: 0 4px 15px rgba(255, 107, 107, 0.4);
}

.btn-primary:hover {
    transform: translateY(-2px);
    box-shadow: 0 6px 25px rgba(255, 107, 107, 0.5);
}

.btn-secondary {
    background: white;
    color: var(--primary-color);
    border: 2px solid var(--primary-color);
}

.btn-secondary:hover {
    background: var(--primary-color);
    color: white;
}

.btn-lg {
    padding: 16px 36px;
    font-size: 18px;
}

.btn-nav {
    padding: 10px 24px;
    font-size: 14px;
}

/* ===== Gradient Text ===== */
.gradient-text {
    background: var(--bg-gradient);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
}

/* ===== Badges ===== */
.badge {
    display: inline-block;
    padding: 8px 16px;
    background: rgba(255, 107, 107, 0.1);
    color: var(--primary-color);
    border-radius: 50px;
    font-size: 14px;
    font-weight: 600;
}

.section-badge {
    display: inline-block;
    padding: 8px 20px;
    background: rgba(255, 107, 107, 0.1);
    color: var(--primary-color);
    border-radius: 50px;
    font-size: 14px;
    font-weight: 600;
    margin-bottom: 16px;
}

/* ===== Navigation ===== */
.navbar {
    position: fixed;
    top: 0;
    left: 0;
    right: 0;
    z-index: 1000;
    padding: 20px 0;
    transition: var(--transition);
}

.navbar.scrolled {
    background: rgba(255, 255, 255, 0.95);
    backdrop-filter: blur(10px);
    padding: 15px 0;
    box-shadow: var(--shadow-sm);
}

.navbar .container {
    display: flex;
    justify-content: space-between;
    align-items: center;
}

.logo {
    font-size: 28px;
    font-weight: 700;
    color: var(--primary-color);
    display: flex;
    align-items: center;
    gap: 8px;
}

.logo-icon {
    font-size: 24px;
}

.nav-links {
    display: flex;
    align-items: center;
    gap: 40px;
}

.nav-links a {
    font-weight: 500;
    color: var(--text-color);
    transition: var(--transition);
}

.nav-links a:hover {
    color: var(--primary-color);
}

.mobile-menu-btn {
    display: none;
    background: none;
    border: none;
    font-size: 24px;
    cursor: pointer;
    color: var(--text-color);
}

/* ===== Hero Section ===== */
.hero {
    background: linear-gradient(135deg, #FFF5F5 0%, #FFF0E6 100%);
    padding: 140px 0 100px;
    position: relative;
    overflow: hidden;
}

.hero-content {
    display: grid;
    grid-template-columns: 1fr 1fr;
    gap: 60px;
    align-items: center;
}

.hero-text h1 {
    font-size: 56px;
    font-weight: 800;
    line-height: 1.2;
    margin-bottom: 24px;
}

.hero-subtitle {
    font-size: 18px;
    color: var(--text-light);
    margin-bottom: 32px;
    max-width: 500px;
}

.hero-cta {
    display: flex;
    gap: 16px;
    margin-bottom: 48px;
    flex-wrap: wrap;
}

.hero-stats {
    display: flex;
    gap: 48px;
}

.stat {
    display: flex;
    flex-direction: column;
}

.stat-number {
    font-size: 32px;
    font-weight: 700;
    color: var(--primary-color);
}

.stat-label {
    font-size: 14px;
    color: var(--text-muted);
}

/* Phone Mockup */
.hero-image {
    position: relative;
}

.phone-mockup {
    position: relative;
    z-index: 2;
}

.phone-frame {
    width: 300px;
    height: 600px;
    background: #1a1a1a;
    border-radius: 40px;
    padding: 12px;
    box-shadow: var(--shadow-lg);
    margin: 0 auto;
}

.phone-screen {
    width: 100%;
    height: 100%;
    background: white;
    border-radius: 32px;
    overflow: hidden;
    padding: 20px;
}

.app-header {
    display: flex;
    justify-content: space-between;
    align-items: center;
    margin-bottom: 20px;
}

.app-logo {
    font-size: 24px;
    font-weight: 700;
    color: var(--primary-color);
}

.header-icons {
    display: flex;
    gap: 16px;
    color: var(--text-muted);
}

.profile-card {
    background: white;
    border-radius: 20px;
    box-shadow: var(--shadow-md);
    padding: 20px;
    text-align: center;
}

.profile-image {
    position: relative;
    width: 120px;
    height: 120px;
    margin: 0 auto 16px;
}

.profile-placeholder {
    width: 100%;
    height: 100%;
    background: linear-gradient(135deg, #FFB6B6 0%, #FF8E8E 100%);
    border-radius: 50%;
    display: flex;
    align-items: center;
    justify-content: center;
    font-size: 48px;
    color: white;
}

.verified-badge {
    position: absolute;
    bottom: 5px;
    right: 5px;
    width: 28px;
    height: 28px;
    background: var(--secondary-color);
    border-radius: 50%;
    display: flex;
    align-items: center;
    justify-content: center;
    color: white;
    font-size: 12px;
    border: 3px solid white;
}

.profile-info h3 {
    font-size: 20px;
    margin-bottom: 4px;
}

.profile-info p {
    font-size: 14px;
    color: var(--text-muted);
    margin-bottom: 8px;
}

.profile-info .bio {
    font-size: 13px;
    color: var(--text-light);
    line-height: 1.5;
}

.compatibility {
    margin-top: 16px;
}

.match-score {
    display: inline-block;
    padding: 8px 20px;
    background: var(--bg-gradient);
    color: white;
    border-radius: 50px;
    font-weight: 600;
    font-size: 14px;
}

.action-buttons {
    display: flex;
    justify-content: center;
    gap: 20px;
    margin-top: 20px;
}

.action-btn {
    width: 56px;
    height: 56px;
    border-radius: 50%;
    border: none;
    display: flex;
    align-items: center;
    justify-content: center;
    font-size: 24px;
    cursor: pointer;
    transition: var(--transition);
    box-shadow: var(--shadow-sm);
}

.action-btn.skip {
    background: white;
    color: #999;
}

.action-btn.super {
    background: var(--accent-color);
    color: white;
}

.action-btn.like {
    background: var(--primary-color);
    color: white;
}

.action-btn:hover {
    transform: scale(1.1);
}

/* Floating Elements */
.floating-elements {
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    pointer-events: none;
}

.floating-card {
    position: absolute;
    background: white;
    padding: 12px 20px;
    border-radius: 50px;
    box-shadow: var(--shadow-md);
    display: flex;
    align-items: center;
    gap: 10px;
    font-size: 14px;
    font-weight: 500;
    animation: float 3s ease-in-out infinite;
}

.floating-card i {
    color: var(--primary-color);
}

.card-1 {
    top: 10%;
    left: -20%;
    animation-delay: 0s;
}

.card-2 {
    top: 40%;
    right: -10%;
    animation-delay: 1s;
}

.card-3 {
    bottom: 20%;
    left: -15%;
    animation-delay: 2s;
}

@keyframes float {
    0%, 100% { transform: translateY(0); }
    50% { transform: translateY(-10px); }
}

.hero-wave {
    position: absolute;
    bottom: 0;
    left: 0;
    right: 0;
}

.hero-wave svg {
    display: block;
    width: 100%;
}

/* ===== Features Section ===== */
.features {
    padding: 100px 0;
    background: white;
}

.section-header {
    text-align: center;
    max-width: 600px;
    margin: 0 auto 60px;
}

.section-header h2 {
    font-size: 42px;
    font-weight: 700;
    margin-bottom: 16px;
}

.section-header p {
    font-size: 18px;
    color: var(--text-light);
}

.features-grid {
    display: grid;
    grid-template-columns: repeat(3, 1fr);
    gap: 32px;
}

.feature-card {
    background: var(--bg-light);
    padding: 40px 32px;
    border-radius: var(--border-radius-lg);
    transition: var(--transition);
    opacity: 0;
    transform: translateY(30px);
}

.feature-card.animate-in {
    opacity: 1;
    transform: translateY(0);
}

.feature-card:hover {
    background: white;
    box-shadow: var(--shadow-lg);
    transform: translateY(-5px);
}

.feature-icon {
    width: 70px;
    height: 70px;
    background: var(--bg-gradient);
    border-radius: 20px;
    display: flex;
    align-items: center;
    justify-content: center;
    font-size: 28px;
    color: white;
    margin-bottom: 24px;
}

.feature-card h3 {
    font-size: 22px;
    font-weight: 600;
    margin-bottom: 12px;
}

.feature-card p {
    color: var(--text-light);
    font-size: 15px;
}

/* ===== How It Works Section ===== */
.how-it-works {
    padding: 100px 0;
    background: var(--bg-light);
}

.steps-container {
    display: flex;
    flex-direction: column;
    gap: 60px;
    max-width: 900px;
    margin: 0 auto;
}

.step {
    display: grid;
    grid-template-columns: 80px 1fr 150px;
    gap: 40px;
    align-items: center;
    opacity: 0;
    transform: translateX(-30px);
}

.step.animate-in {
    opacity: 1;
    transform: translateX(0);
}

.step:nth-child(even) {
    transform: translateX(30px);
}

.step:nth-child(even).animate-in {
    transform: translateX(0);
}

.step-number {
    width: 80px;
    height: 80px;
    background: var(--bg-gradient);
    border-radius: 50%;
    display: flex;
    align-items: center;
    justify-content: center;
    font-size: 32px;
    font-weight: 700;
    color: white;
}

.step-content h3 {
    font-size: 24px;
    font-weight: 600;
    margin-bottom: 12px;
}

.step-content p {
    color: var(--text-light);
    font-size: 16px;
}

.mini-phone {
    width: 100px;
    height: 180px;
    background: #1a1a1a;
    border-radius: 20px;
    padding: 8px;
}

.mini-screen {
    width: 100%;
    height: 100%;
    background: var(--bg-gradient);
    border-radius: 14px;
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    gap: 10px;
    color: white;
}

.mini-screen i {
    font-size: 32px;
}

.mini-screen span {
    font-size: 12px;
    font-weight: 600;
}

/* ===== Testimonials Section ===== */
.testimonials {
    padding: 100px 0;
    background: white;
}

.testimonials-grid {
    display: grid;
    grid-template-columns: repeat(3, 1fr);
    gap: 32px;
}

.testimonial-card {
    background: var(--bg-light);
    padding: 32px;
    border-radius: var(--border-radius-lg);
    transition: var(--transition);
    opacity: 0;
    transform: translateY(30px);
}

.testimonial-card.animate-in {
    opacity: 1;
    transform: translateY(0);
}

.testimonial-card.featured {
    grid-column: span 2;
    grid-row: span 2;
    display: flex;
    flex-direction: column;
    background: linear-gradient(135deg, #FFF5F5 0%, #FFE6E6 100%);
}

.couple-image {
    margin-bottom: 24px;
}

.couple-placeholder {
    width: 100%;
    height: 200px;
    background: linear-gradient(135deg, #FFB6B6 0%, #FF8E8E 100%);
    border-radius: var(--border-radius);
    display: flex;
    align-items: center;
    justify-content: center;
    font-size: 64px;
    color: white;
}

.stars {
    display: flex;
    gap: 4px;
    margin-bottom: 16px;
    color: var(--accent-color);
}

.quote {
    font-size: 16px;
    color: var(--text-light);
    line-height: 1.7;
    margin-bottom: 20px;
    font-style: italic;
}

.featured .quote {
    font-size: 18px;
    flex-grow: 1;
}

.testimonial-author strong {
    display: block;
    font-size: 16px;
    margin-bottom: 4px;
}

.testimonial-author span {
    font-size: 14px;
    color: var(--text-muted);
}

/* ===== Social Proof Section ===== */
.social-proof {
    padding: 60px 0;
    background: var(--bg-light);
}

.press-logos {
    display: flex;
    align-items: center;
    justify-content: center;
    gap: 40px;
    flex-wrap: wrap;
}

.press-label {
    font-size: 14px;
    color: var(--text-muted);
    font-weight: 500;
}

.logos-grid {
    display: flex;
    gap: 60px;
    align-items: center;
}

.press-logo {
    font-size: 24px;
    font-weight: 700;
    color: var(--text-muted);
    opacity: 0.5;
}

/* ===== Download CTA Section ===== */
.download-cta {
    padding: 100px 0;
    background: linear-gradient(135deg, #FF6B6B 0%, #FF8E53 100%);
    color: white;
    position: relative;
    overflow: hidden;
}

.download-cta .container {
    display: grid;
    grid-template-columns: 1fr 1fr;
    gap: 60px;
    align-items: center;
}

.cta-content h2 {
    font-size: 42px;
    font-weight: 700;
    margin-bottom: 20px;
}

.cta-content .gradient-text {
    background: white;
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
}

.cta-content p {
    font-size: 18px;
    opacity: 0.9;
    margin-bottom: 32px;
    max-width: 500px;
}

.download-buttons {
    display: flex;
    gap: 20px;
    margin-bottom: 32px;
    flex-wrap: wrap;
}

.download-btn {
    display: flex;
    align-items: center;
    gap: 12px;
    padding: 14px 28px;
    background: rgba(0, 0, 0, 0.2);
    border-radius: 12px;
    transition: var(--transition);
}

.download-btn:hover {
    background: rgba(0, 0, 0, 0.3);
    transform: translateY(-2px);
}

.download-btn i {
    font-size: 32px;
}

.btn-text {
    display: flex;
    flex-direction: column;
    text-align: left;
}

.btn-text .small {
    font-size: 11px;
    opacity: 0.8;
}

.btn-text .big {
    font-size: 18px;
    font-weight: 600;
}

.app-ratings {
    display: flex;
    gap: 32px;
    flex-wrap: wrap;
}

.rating {
    display: flex;
    flex-direction: column;
    gap: 6px;
}

.rating .stars {
    color: white;
}

.rating span {
    font-size: 13px;
    opacity: 0.9;
}

.phone-stack {
    position: relative;
    height: 400px;
}

.cta-phone {
    position: absolute;
    width: 200px;
    height: 400px;
    background: #1a1a1a;
    border-radius: 30px;
    display: flex;
    align-items: center;
    justify-content: center;
    box-shadow: var(--shadow-lg);
}

.cta-phone .phone-content {
    width: 180px;
    height: 380px;
    background: white;
    border-radius: 24px;
    display: flex;
    align-items: center;
    justify-content: center;
    font-size: 64px;
    color: var(--primary-color);
}

.phone-back {
    left: 50px;
    transform: rotate(-10deg);
}

.phone-front {
    left: 120px;
    z-index: 2;
    transform: rotate(5deg);
}

/* ===== Newsletter Section ===== */
.newsletter {
    padding: 80px 0;
    background: var(--dark-color);
    color: white;
}

.newsletter-content {
    text-align: center;
    max-width: 600px;
    margin: 0 auto;
}

.newsletter-content h3 {
    font-size: 28px;
    margin-bottom: 12px;
}

.newsletter-content p {
    opacity: 0.8;
    margin-bottom: 32px;
}

.newsletter-form {
    display: flex;
    gap: 12px;
    justify-content: center;
    margin-bottom: 16px;
}

.newsletter-form input {
    padding: 16px 24px;
    border: none;
    border-radius: 50px;
    font-size: 16px;
    width: 300px;
    font-family: inherit;
}

.newsletter-form input:focus {
    outline: none;
    box-shadow: 0 0 0 3px rgba(255, 107, 107, 0.3);
}

.privacy-note {
    font-size: 13px;
    opacity: 0.6;
}

/* ===== Footer ===== */
.footer {
    padding: 80px 0 40px;
    background: #1a1a2e;
    color: white;
}

.footer-grid {
    display: grid;
    grid-template-columns: 2fr 1fr 1fr 1fr;
    gap: 60px;
    margin-bottom: 60px;
}

.footer-brand p {
    opacity: 0.7;
    margin: 20px 0;
    max-width: 300px;
}

.footer-brand .logo {
    color: white;
}

.social-links {
    display: flex;
    gap: 16px;
}

.social-links a {
    width: 40px;
    height: 40px;
    background: rgba(255, 255, 255, 0.1);
    border-radius: 50%;
    display: flex;
    align-items: center;
    justify-content: center;
    transition: var(--transition);
}

.social-links a:hover {
    background: var(--primary-color);
}

.footer-links h4 {
    font-size: 16px;
    font-weight: 600;
    margin-bottom: 24px;
}

.footer-links ul {
    display: flex;
    flex-direction: column;
    gap: 12px;
}

.footer-links a {
    opacity: 0.7;
    transition: var(--transition);
}

.footer-links a:hover {
    opacity: 1;
    color: var(--primary-color);
}

.footer-bottom {
    padding-top: 40px;
    border-top: 1px solid rgba(255, 255, 255, 0.1);
    display: flex;
    justify-content: space-between;
    align-items: center;
    flex-wrap: wrap;
    gap: 20px;
}

.footer-bottom p {
    opacity: 0.6;
    font-size: 14px;
}

.footer-badges {
    display: flex;
    gap: 20px;
}

.footer-badges .badge {
    background: rgba(255, 255, 255, 0.1);
    color: white;
    font-size: 12px;
    display: flex;
    align-items: center;
    gap: 6px;
}

/* ===== Responsive Design ===== */
@media (max-width: 1024px) {
    .hero-content {
        grid-template-columns: 1fr;
        text-align: center;
    }

    .hero-text h1 {
        font-size: 42px;
    }

    .hero-subtitle {
        margin: 0 auto 32px;
    }

    .hero-cta {
        justify-content: center;
    }

    .hero-stats {
        justify-content: center;
    }

    .hero-image {
        order: -1;
    }

    .floating-elements {
        display: none;
    }

    .features-grid {
        grid-template-columns: repeat(2, 1fr);
    }

    .testimonials-grid {
        grid-template-columns: 1fr;
    }

    .testimonial-card.featured {
        grid-column: span 1;
        grid-row: span 1;
    }

    .download-cta .container {
        grid-template-columns: 1fr;
        text-align: center;
    }

    .cta-content p {
        margin: 0 auto 32px;
    }

    .download-buttons {
        justify-content: center;
    }

    .app-ratings {
        justify-content: center;
    }

    .cta-phones {
        display: none;
    }

    .footer-grid {
        grid-template-columns: repeat(2, 1fr);
    }
}

@media (max-width: 768px) {
    .nav-links {
        display: none;
        position: absolute;
        top: 100%;
        left: 0;
        right: 0;
        background: white;
        flex-direction: column;
        padding: 20px;
        gap: 20px;
        box-shadow: var(--shadow-md);
    }

    .nav-links.active {
        display: flex;
    }

    .mobile-menu-btn {
        display: block;
    }

    .hero-text h1 {
        font-size: 32px;
    }

    .hero-cta {
        flex-direction: column;
        align-items: center;
    }

    .btn-lg {
        width: 100%;
        justify-content: center;
    }

    .hero-stats {
        flex-direction: column;
        gap: 24px;
    }

    .phone-frame {
        width: 250px;
        height: 500px;
    }

    .features-grid {
        grid-template-columns: 1fr;
    }

    .step {
        grid-template-columns: 1fr;
        text-align: center;
        gap: 20px;
    }

    .step-number {
        margin: 0 auto;
    }

    .step-image {
        display: none;
    }

    .section-header h2 {
        font-size: 32px;
    }

    .cta-content h2 {
        font-size: 32px;
    }

    .newsletter-form {
        flex-direction: column;
        align-items: center;
    }

    .newsletter-form input {
        width: 100%;
        max-width: 300px;
    }

    .footer-grid {
        grid-template-columns: 1fr;
        gap: 40px;
        text-align: center;
    }

    .social-links {
        justify-content: center;
    }

    .footer-bottom {
        flex-direction: column;
        text-align: center;
    }

    .footer-badges {
        flex-wrap: wrap;
        justify-content: center;
    }
}

@media (max-width: 480px) {
    .hero {
        padding: 120px 0 80px;
    }

    .hero-text h1 {
        font-size: 28px;
    }

    .download-buttons {
        flex-direction: column;
    }

    .download-btn {
        width: 100%;
        justify-content: center;
    }
}

/* ===== Animations ===== */
@keyframes fadeInUp {
    from {
        opacity: 0;
        transform: translateY(30px);
    }
    to {
        opacity: 1;
        transform: translateY(0);
    }
}

.feature-card,
.step,
.testimonial-card {
    transition: opacity 0.6s ease, transform 0.6s ease;
}

toolUseResult.content:
/* ===== CSS Variables ===== */
:root {
    --primary-color: #FF6B6B;
    --primary-dark: #E55555;
    --primary-light: #FF8E8E;
    --secondary-color: #4ECDC4;
    --accent-color: #FFE66D;
    --dark-color: #2C3E50;
    --text-color: #333333;
    --text-light: #666666;
    --text-muted: #999999;
    --bg-color: #FFFFFF;
    --bg-light: #F8F9FA;
    --bg-gradient: linear-gradient(135deg, #FF6B6B 0%, #FF8E53 100%);
    --shadow-sm: 0 2px 10px rgba(0, 0, 0, 0.08);
    --shadow-md: 0 5px 25px rgba(0, 0, 0, 0.1);
    --shadow-lg: 0 10px 50px rgba(0, 0, 0, 0.15);
    --border-radius: 12px;
    --border-radius-lg: 24px;
    --transition: all 0.3s ease;
}

/* ===== Reset & Base ===== */
* {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
}

html {
    scroll-behavior: smooth;
}

body {
    font-family: 'Poppins', sans-serif;
    color: var(--text-color);
    line-height: 1.6;
    overflow-x: hidden;
}

.container {
    max-width: 1200px;
    margin: 0 auto;
    padding: 0 20px;
}

a {
    text-decoration: none;
    color: inherit;
}

ul {
    list-style: none;
}

/* ===== Buttons ===== */
.btn {
    display: inline-flex;
    align-items: center;
    gap: 8px;
    padding: 12px 28px;
    border-radius: 50px;
    font-weight: 600;
    font-size: 16px;
    transition: var(--transition);
    cursor: pointer;
    border: none;
}

.btn-primary {
    background: var(--bg-gradient);
    color: white;
    box-shadow: 0 4px 15px rgba(255, 107, 107, 0.4);
}

.btn-primary:hover {
    transform: translateY(-2px);
    box-shadow: 0 6px 25px rgba(255, 107, 107, 0.5);
}

.btn-secondary {
    background: white;
    color: var(--primary-color);
    border: 2px solid var(--primary-color);
}

.btn-secondary:hover {
    background: var(--primary-color);
    color: white;
}

.btn-lg {
    padding: 16px 36px;
    font-size: 18px;
}

.btn-nav {
    padding: 10px 24px;
    font-size: 14px;
}

/* ===== Gradient Text ===== */
.gradient-text {
    background: var(--bg-gradient);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
}

/* ===== Badges ===== */
.badge {
    display: inline-block;
    padding: 8px 16px;
    background: rgba(255, 107, 107, 0.1);
    color: var(--primary-color);
    border-radius: 50px;
    font-size: 14px;
    font-weight: 600;
}

.section-badge {
    display: inline-block;
    padding: 8px 20px;
    background: rgba(255, 107, 107, 0.1);
    color: var(--primary-color);
    border-radius: 50px;
    font-size: 14px;
    font-weight: 600;
    margin-bottom: 16px;
}

/* ===== Navigation ===== */
.navbar {
    position: fixed;
    top: 0;
    left: 0;
    right: 0;
    z-index: 1000;
    padding: 20px 0;
    transition: var(--transition);
}

.navbar.scrolled {
    background: rgba(255, 255, 255, 0.95);
    backdrop-filter: blur(10px);
    padding: 15px 0;
    box-shadow: var(--shadow-sm);
}

.navbar .container {
    display: flex;
    justify-content: space-between;
    align-items: center;
}

.logo {
    font-size: 28px;
    font-weight: 700;
    color: var(--primary-color);
    display: flex;
    align-items: center;
    gap: 8px;
}

.logo-icon {
    font-size: 24px;
}

.nav-links {
    display: flex;
    align-items: center;
    gap: 40px;
}

.nav-links a {
    font-weight: 500;
    color: var(--text-color);
    transition: var(--transition);
}

.nav-links a:hover {
    color: var(--primary-color);
}

.mobile-menu-btn {
    display: none;
    background: none;
    border: none;
    font-size: 24px;
    cursor: pointer;
    color: var(--text-color);
}

/* ===== Hero Section ===== */
.hero {
    background: linear-gradient(135deg, #FFF5F5 0%, #FFF0E6 100%);
    padding: 140px 0 100px;
    position: relative;
    overflow: hidden;
}

.hero-content {
    display: grid;
    grid-template-columns: 1fr 1fr;
    gap: 60px;
    align-items: center;
}

.hero-text h1 {
    font-size: 56px;
    font-weight: 800;
    line-height: 1.2;
    margin-bottom: 24px;
}

.hero-subtitle {
    font-size: 18px;
    color: var(--text-light);
    margin-bottom: 32px;
    max-width: 500px;
}

.hero-cta {
    display: flex;
    gap: 16px;
    margin-bottom: 48px;
    flex-wrap: wrap;
}

.hero-stats {
    display: flex;
    gap: 48px;
}

.stat {
    display: flex;
    flex-direction: column;
}

.stat-number {
    font-size: 32px;
    font-weight: 700;
    color: var(--primary-color);
}

.stat-label {
    font-size: 14px;
    color: var(--text-muted);
}

/* Phone Mockup */
.hero-image {
    position: relative;
}

.phone-mockup {
    position: relative;
    z-index: 2;
}

.phone-frame {
    width: 300px;
    height: 600px;
    background: #1a1a1a;
    border-radius: 40px;
    padding: 12px;
    box-shadow: var(--shadow-lg);
    margin: 0 auto;
}

.phone-screen {
    width: 100%;
    height: 100%;
    background: white;
    border-radius: 32px;
    overflow: hidden;
    padding: 20px;
}

.app-header {
    display: flex;
    justify-content: space-between;
    align-items: center;
    margin-bottom: 20px;
}

.app-logo {
    font-size: 24px;
    font-weight: 700;
    color: var(--primary-color);
}

.header-icons {
    display: flex;
    gap: 16px;
    color: var(--text-muted);
}

.profile-card {
    background: white;
    border-radius: 20px;
    box-shadow: var(--shadow-md);
    padding: 20px;
    text-align: center;
}

.profile-image {
    position: relative;
    width: 120px;
    height: 120px;
    margin: 0 auto 16px;
}

.profile-placeholder {
    width: 100%;
    height: 100%;
    background: linear-gradient(135deg, #FFB6B6 0%, #FF8E8E 100%);
    border-radius: 50%;
    display: flex;
    align-items: center;
    justify-content: center;
    font-size: 48px;
    color: white;
}

.verified-badge {
    position: absolute;
    bottom: 5px;
    right: 5px;
    width: 28px;
    height: 28px;
    background: var(--secondary-color);
    border-radius: 50%;
    display: flex;
    align-items: center;
    justify-content: center;
    color: white;
    font-size: 12px;
    border: 3px solid white;
}

.profile-info h3 {
    font-size: 20px;
    margin-bottom: 4px;
}

.profile-info p {
    font-size: 14px;
    color: var(--text-muted);
    margin-bottom: 8px;
}

.profile-info .bio {
    font-size: 13px;
    color: var(--text-light);
    line-height: 1.5;
}

.compatibility {
    margin-top: 16px;
}

.match-score {
    display: inline-block;
    padding: 8px 20px;
    background: var(--bg-gradient);
    color: white;
    border-radius: 50px;
    font-weight: 600;
    font-size: 14px;
}

.action-buttons {
    display: flex;
    justify-content: center;
    gap: 20px;
    margin-top: 20px;
}

.action-btn {
    width: 56px;
    height: 56px;
    border-radius: 50%;
    border: none;
    display: flex;
    align-items: center;
    justify-content: center;
    font-size: 24px;
    cursor: pointer;
    transition: var(--transition);
    box-shadow: var(--shadow-sm);
}

.action-btn.skip {
    background: white;
    color: #999;
}

.action-btn.super {
    background: var(--accent-color);
    color: white;
}

.action-btn.like {
    background: var(--primary-color);
    color: white;
}

.action-btn:hover {
    transform: scale(1.1);
}

/* Floating Elements */
.floating-elements {
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    pointer-events: none;
}

.floating-card {
    position: absolute;
    background: white;
    padding: 12px 20px;
    border-radius: 50px;
    box-shadow: var(--shadow-md);
    display: flex;
    align-items: center;
    gap: 10px;
    font-size: 14px;
    font-weight: 500;
    animation: float 3s ease-in-out infinite;
}

.floating-card i {
    color: var(--primary-color);
}

.card-1 {
    top: 10%;
    left: -20%;
    animation-delay: 0s;
}

.card-2 {
    top: 40%;
    right: -10%;
    animation-delay: 1s;
}

.card-3 {
    bottom: 20%;
    left: -15%;
    animation-delay: 2s;
}

@keyframes float {
    0%, 100% { transform: translateY(0); }
    50% { transform: translateY(-10px); }
}

.hero-wave {
    position: absolute;
    bottom: 0;
    left: 0;
    right: 0;
}

.hero-wave svg {
    display: block;
    width: 100%;
}

/* ===== Features Section ===== */
.features {
    padding: 100px 0;
    background: white;
}

.section-header {
    text-align: center;
    max-width: 600px;
    margin: 0 auto 60px;
}

.section-header h2 {
    font-size: 42px;
    font-weight: 700;
    margin-bottom: 16px;
}

.section-header p {
    font-size: 18px;
    color: var(--text-light);
}

.features-grid {
    display: grid;
    grid-template-columns: repeat(3, 1fr);
    gap: 32px;
}

.feature-card {
    background: var(--bg-light);
    padding: 40px 32px;
    border-radius: var(--border-radius-lg);
    transition: var(--transition);
    opacity: 0;
    transform: translateY(30px);
}

.feature-card.animate-in {
    opacity: 1;
    transform: translateY(0);
}

.feature-card:hover {
    background: white;
    box-shadow: var(--shadow-lg);
    transform: translateY(-5px);
}

.feature-icon {
    width: 70px;
    height: 70px;
    background: var(--bg-gradient);
    border-radius: 20px;
    display: flex;
    align-items: center;
    justify-content: center;
    font-size: 28px;
    color: white;
    margin-bottom: 24px;
}

.feature-card h3 {
    font-size: 22px;
    font-weight: 600;
    margin-bottom: 12px;
}

.feature-card p {
    color: var(--text-light);
    font-size: 15px;
}

/* ===== How It Works Section ===== */
.how-it-works {
    padding: 100px 0;
    background: var(--bg-light);
}

.steps-container {
    display: flex;
    flex-direction: column;
    gap: 60px;
    max-width: 900px;
    margin: 0 auto;
}

.step {
    display: grid;
    grid-template-columns: 80px 1fr 150px;
    gap: 40px;
    align-items: center;
    opacity: 0;
    transform: translateX(-30px);
}

.step.animate-in {
    opacity: 1;
    transform: translateX(0);
}

.step:nth-child(even) {
    transform: translateX(30px);
}

.step:nth-child(even).animate-in {
    transform: translateX(0);
}

.step-number {
    width: 80px;
    height: 80px;
    background: var(--bg-gradient);
    border-radius: 50%;
    display: flex;
    align-items: center;
    justify-content: center;
    font-size: 32px;
    font-weight: 700;
    color: white;
}

.step-content h3 {
    font-size: 24px;
    font-weight: 600;
    margin-bottom: 12px;
}

.step-content p {
    color: var(--text-light);
    font-size: 16px;
}

.mini-phone {
    width: 100px;
    height: 180px;
    background: #1a1a1a;
    border-radius: 20px;
    padding: 8px;
}

.mini-screen {
    width: 100%;
    height: 100%;
    background: var(--bg-gradient);
    border-radius: 14px;
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    gap: 10px;
    color: white;
}

.mini-screen i {
    font-size: 32px;
}

.mini-screen span {
    font-size: 12px;
    font-weight: 600;
}

/* ===== Testimonials Section ===== */
.testimonials {
    padding: 100px 0;
    background: white;
}

.testimonials-grid {
    display: grid;
    grid-template-columns: repeat(3, 1fr);
    gap: 32px;
}

.testimonial-card {
    background: var(--bg-light);
    padding: 32px;
    border-radius: var(--border-radius-lg);
    transition: var(--transition);
    opacity: 0;
    transform: translateY(30px);
}

.testimonial-card.animate-in {
    opacity: 1;
    transform: translateY(0);
}

.testimonial-card.featured {
    grid-column: span 2;
    grid-row: span 2;
    display: flex;
    flex-direction: column;
    background: linear-gradient(135deg, #FFF5F5 0%, #FFE6E6 100%);
}

.couple-image {
    margin-bottom: 24px;
}

.couple-placeholder {
    width: 100%;
    height: 200px;
    background: linear-gradient(135deg, #FFB6B6 0%, #FF8E8E 100%);
    border-radius: var(--border-radius);
    display: flex;
    align-items: center;
    justify-content: center;
    font-size: 64px;
    color: white;
}

.stars {
    display: flex;
    gap: 4px;
    margin-bottom: 16px;
    color: var(--accent-color);
}

.quote {
    font-size: 16px;
    color: var(--text-light);
    line-height: 1.7;
    margin-bottom: 20px;
    font-style: italic;
}

.featured .quote {
    font-size: 18px;
    flex-grow: 1;
}

.testimonial-author strong {
    display: block;
    font-size: 16px;
    margin-bottom: 4px;
}

.testimonial-author span {
    font-size: 14px;
    color: var(--text-muted);
}

/* ===== Social Proof Section ===== */
.social-proof {
    padding: 60px 0;
    background: var(--bg-light);
}

.press-logos {
    display: flex;
    align-items: center;
    justify-content: center;
    gap: 40px;
    flex-wrap: wrap;
}

.press-label {
    font-size: 14px;
    color: var(--text-muted);
    font-weight: 500;
}

.logos-grid {
    display: flex;
    gap: 60px;
    align-items: center;
}

.press-logo {
    font-size: 24px;
    font-weight: 700;
    color: var(--text-muted);
    opacity: 0.5;
}

/* ===== Download CTA Section ===== */
.download-cta {
    padding: 100px 0;
    background: linear-gradient(135deg, #FF6B6B 0%, #FF8E53 100%);
    color: white;
    position: relative;
    overflow: hidden;
}

.download-cta .container {
    display: grid;
    grid-template-columns: 1fr 1fr;
    gap: 60px;
    align-items: center;
}

.cta-content h2 {
    font-size: 42px;
    font-weight: 700;
    margin-bottom: 20px;
}

.cta-content .gradient-text {
    background: white;
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
}

.cta-content p {
    font-size: 18px;
    opacity: 0.9;
    margin-bottom: 32px;
    max-width: 500px;
}

.download-buttons {
    display: flex;
    gap: 20px;
    margin-bottom: 32px;
    flex-wrap: wrap;
}

.download-btn {
    display: flex;
    align-items: center;
    gap: 12px;
    padding: 14px 28px;
    background: rgba(0, 0, 0, 0.2);
    border-radius: 12px;
    transition: var(--transition);
}

.download-btn:hover {
    background: rgba(0, 0, 0, 0.3);
    transform: translateY(-2px);
}

.download-btn i {
    font-size: 32px;
}

.btn-text {
    display: flex;
    flex-direction: column;
    text-align: left;
}

.btn-text .small {
    font-size: 11px;
    opacity: 0.8;
}

.btn-text .big {
    font-size: 18px;
    font-weight: 600;
}

.app-ratings {
    display: flex;
    gap: 32px;
    flex-wrap: wrap;
}

.rating {
    display: flex;
    flex-direction: column;
    gap: 6px;
}

.rating .stars {
    color: white;
}

.rating span {
    font-size: 13px;
    opacity: 0.9;
}

.phone-stack {
    position: relative;
    height: 400px;
}

.cta-phone {
    position: absolute;
    width: 200px;
    height: 400px;
    background: #1a1a1a;
    border-radius: 30px;
    display: flex;
    align-items: center;
    justify-content: center;
    box-shadow: var(--shadow-lg);
}

.cta-phone .phone-content {
    width: 180px;
    height: 380px;
    background: white;
    border-radius: 24px;
    display: flex;
    align-items: center;
    justify-content: center;
    font-size: 64px;
    color: var(--primary-color);
}

.phone-back {
    left: 50px;
    transform: rotate(-10deg);
}

.phone-front {
    left: 120px;
    z-index: 2;
    transform: rotate(5deg);
}

/* ===== Newsletter Section ===== */
.newsletter {
    padding: 80px 0;
    background: var(--dark-color);
    color: white;
}

.newsletter-content {
    text-align: center;
    max-width: 600px;
    margin: 0 auto;
}

.newsletter-content h3 {
    font-size: 28px;
    margin-bottom: 12px;
}

.newsletter-content p {
    opacity: 0.8;
    margin-bottom: 32px;
}

.newsletter-form {
    display: flex;
    gap: 12px;
    justify-content: center;
    margin-bottom: 16px;
}

.newsletter-form input {
    padding: 16px 24px;
    border: none;
    border-radius: 50px;
    font-size: 16px;
    width: 300px;
    font-family: inherit;
}

.newsletter-form input:focus {
    outline: none;
    box-shadow: 0 0 0 3px rgba(255, 107, 107, 0.3);
}

.privacy-note {
    font-size: 13px;
    opacity: 0.6;
}

/* ===== Footer ===== */
.footer {
    padding: 80px 0 40px;
    background: #1a1a2e;
    color: white;
}

.footer-grid {
    display: grid;
    grid-template-columns: 2fr 1fr 1fr 1fr;
    gap: 60px;
    margin-bottom: 60px;
}

.footer-brand p {
    opacity: 0.7;
    margin: 20px 0;
    max-width: 300px;
}

.footer-brand .logo {
    color: white;
}

.social-links {
    display: flex;
    gap: 16px;
}

.social-links a {
    width: 40px;
    height: 40px;
    background: rgba(255, 255, 255, 0.1);
    border-radius: 50%;
    display: flex;
    align-items: center;
    justify-content: center;
    transition: var(--transition);
}

.social-links a:hover {
    background: var(--primary-color);
}

.footer-links h4 {
    font-size: 16px;
    font-weight: 600;
    margin-bottom: 24px;
}

.footer-links ul {
    display: flex;
    flex-direction: column;
    gap: 12px;
}

.footer-links a {
    opacity: 0.7;
    transition: var(--transition);
}

.footer-links a:hover {
    opacity: 1;
    color: var(--primary-color);
}

.footer-bottom {
    padding-top: 40px;
    border-top: 1px solid rgba(255, 255, 255, 0.1);
    display: flex;
    justify-content: space-between;
    align-items: center;
    flex-wrap: wrap;
    gap: 20px;
}

.footer-bottom p {
    opacity: 0.6;
    font-size: 14px;
}

.footer-badges {
    display: flex;
    gap: 20px;
}

.footer-badges .badge {
    background: rgba(255, 255, 255, 0.1);
    color: white;
    font-size: 12px;
    display: flex;
    align-items: center;
    gap: 6px;
}

/* ===== Responsive Design ===== */
@media (max-width: 1024px) {
    .hero-content {
        grid-template-columns: 1fr;
        text-align: center;
    }

    .hero-text h1 {
        font-size: 42px;
    }

    .hero-subtitle {
        margin: 0 auto 32px;
    }

    .hero-cta {
        justify-content: center;
    }

    .hero-stats {
        justify-content: center;
    }

    .hero-image {
        order: -1;
    }

    .floating-elements {
        display: none;
    }

    .features-grid {
        grid-template-columns: repeat(2, 1fr);
    }

    .testimonials-grid {
        grid-template-columns: 1fr;
    }

    .testimonial-card.featured {
        grid-column: span 1;
        grid-row: span 1;
    }

    .download-cta .container {
        grid-template-columns: 1fr;
        text-align: center;
    }

    .cta-content p {
        margin: 0 auto 32px;
    }

    .download-buttons {
        justify-content: center;
    }

    .app-ratings {
        justify-content: center;
    }

    .cta-phones {
        display: none;
    }

    .footer-grid {
        grid-template-columns: repeat(2, 1fr);
    }
}

@media (max-width: 768px) {
    .nav-links {
        display: none;
        position: absolute;
        top: 100%;
        left: 0;
        right: 0;
        background: white;
        flex-direction: column;
        padding: 20px;
        gap: 20px;
        box-shadow: var(--shadow-md);
    }

    .nav-links.active {
        display: flex;
    }

    .mobile-menu-btn {
        display: block;
    }

    .hero-text h1 {
        font-size: 32px;
    }

    .hero-cta {
        flex-direction: column;
        align-items: center;
    }

    .btn-lg {
        width: 100%;
        justify-content: center;
    }

    .hero-stats {
        flex-direction: column;
        gap: 24px;
    }

    .phone-frame {
        width: 250px;
        height: 500px;
    }

    .features-grid {
        grid-template-columns: 1fr;
    }

    .step {
        grid-template-columns: 1fr;
        text-align: center;
        gap: 20px;
    }

    .step-number {
        margin: 0 auto;
    }

    .step-image {
        display: none;
    }

    .section-header h2 {
        font-size: 32px;
    }

    .cta-content h2 {
        font-size: 32px;
    }

    .newsletter-form {
        flex-direction: column;
        align-items: center;
    }

    .newsletter-form input {
        width: 100%;
        max-width: 300px;
    }

    .footer-grid {
        grid-template-columns: 1fr;
        gap: 40px;
        text-align: center;
    }

    .social-links {
        justify-content: center;
    }

    .footer-bottom {
        flex-direction: column;
        text-align: center;
    }

    .footer-badges {
        flex-wrap: wrap;
        justify-content: center;
    }
}

@media (max-width: 480px) {
    .hero {
        padding: 120px 0 80px;
    }

    .hero-text h1 {
        font-size: 28px;
    }

    .download-buttons {
        flex-direction: column;
    }

    .download-btn {
        width: 100%;
        justify-content: center;
    }
}

/* ===== Animations ===== */
@keyframes fadeInUp {
    from {
        opacity: 0;
        transform: translateY(30px);
    }
    to {
        opacity: 1;
        transform: translateY(0);
    }
}

.feature-card,
.step,
.testimonial-card {
    transition: opacity 0.6s ease, transform 0.6s ease;
}

